INSERT INTO `default_def_page_fields` VALUES (1,'2014-08-23 11:37:59','2014-08-26 03:14:13',1,NULL,'<p>Welcome to our homepage. We have not quite finished setting up our website yet, but please add us to your bookmarks and come back soon.</p>\r\n'),(2,'2014-08-23 11:37:59',NULL,1,NULL,'<p>To contact us please fill out the form below.</p>\n				{{ contact:form name=\"text|required\" email=\"text|required|valid_email\" subject=\"dropdown|Support|Sales|Feedback|Other\" message=\"textarea\" attachment=\"file|zip\" }}\n					<div><label for=\"name\">Name:</label>{{ name }}</div>\n					<div><label for=\"email\">Email:</label>{{ email }}</div>\n					<div><label for=\"subject\">Subject:</label>{{ subject }}</div>\n					<div><label for=\"message\">Message:</label>{{ message }}</div>\n					<div><label for=\"attachment\">Attach  a zip file:</label>{{ attachment }}</div>\n				{{ /contact:form }}'),(3,'2014-08-23 11:37:59',NULL,1,NULL,'{{ search:form class=\"search-form\" }} \n		<input name=\"q\" placeholder=\"Search terms...\" />\n	{{ /search:form }}'),(4,'2014-08-23 11:37:59',NULL,1,NULL,'{{ search:form class=\"search-form\" }} \n		<input name=\"q\" placeholder=\"Search terms...\" />\n	{{ /search:form }}\n\n{{ search:results }}\n\n	{{ total }} results for \"{{ query }}\".\n\n	<hr />\n\n	{{ entries }}\n\n		<article>\n			<h4>{{ singular }}: <a href=\"{{ url }}\">{{ title }}</a></h4>\n			<p>{{ description }}</p>\n		</article>\n\n	{{ /entries }}\n\n        {{ pagination }}\n\n{{ /search:results }}'),(5,'2014-08-23 11:37:59',NULL,1,NULL,'<p>We cannot find the page you are looking for, please click <a title=\"Home\" href=\"{{ pages:url id=\'1\' }}\">here</a> to go to the homepage.</p>'),(6,'2014-08-25 03:33:06','2014-08-25 04:02:22',1,1,'draft-slideshow'),(7,'2014-08-26 03:52:29','2014-08-26 05:10:07',1,2,'This page is root of list of events'),(8,'2014-08-26 13:13:53','2014-08-27 16:53:08',1,3,NULL),(11,'2014-08-28 20:36:51','2014-09-04 12:26:58',1,4,NULL),(12,'2014-09-02 01:30:18','2014-10-28 14:34:34',1,5,'<p style=\"font-size: 13px; line-height: 20.7999992370605px; text-align: center;\"><img src=\"http://tdc.edu.vn/Resources/Images/Article/TDC/Gioithieu/SAN-TRUONG.png\" style=\"height: 219px; width: 300px;\" /><img src=\"http://tdc.edu.vn/Resources/Images/Article/TDC/Gioithieu/DSC_0010.png\" style=\"height: 219px; width: 300px;\" /></p>\r\n\r\n<p style=\"font-size: 13px; line-height: 20.7999992370605px; text-align: center;\"><img src=\"http://tdc.edu.vn/Resources/Images/Article/TDC/Gioithieu/2.png\" style=\"height: 219px; width: 300px;\" /><img src=\"http://tdc.edu.vn/Resources/Images/Article/TDC/Gioithieu/3.png\" style=\"height: 219px; width: 300px;\" /></p>\r\n\r\n<p style=\"font-size: 13px; line-height: 20.7999992370605px;\"><span style=\"color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif; line-height: 20.7999992370605px;\">Trường Cao đẳng C&ocirc;ng nghệ Thủ Đức lu&ocirc;n mang đến một h&igrave;nh ảnh năng động, s&aacute;ng tạo, hiện đại cho giới sinh vi&ecirc;n v&agrave; đội ngũ giảng vi&ecirc;n với bề d&agrave;y hơn 30 năm sự nghiệp trồng người.&nbsp;</span></p>\r\n\r\n<p style=\"font-size: 13px; line-height: 20.7999992370605px; text-align: center;\"><img src=\"http://tdc.edu.vn/Resources/Images/Article/TDC/Gioithieu/D.png\" style=\"height: 219px; width: 300px;\" /><img src=\"http://tdc.edu.vn/Resources/Images/Article/TDC/Gioithieu/E.png\" style=\"height: 219px; width: 300px;\" /></p>\r\n\r\n<p style=\"font-size: 13px; line-height: 20.7999992370605px; text-align: center;\"><img src=\"http://tdc.edu.vn/Resources/Images/Article/TDC/Gioithieu/C.png\" style=\"height: 219px; width: 300px;\" /><img src=\"http://tdc.edu.vn/Resources/Images/Article/TDC/Gioithieu/H.png\" style=\"height: 219px; width: 300px;\" /></p>\r\n\r\n<p style=\"font-size: 13px; line-height: 20.7999992370605px;\"><span style=\"color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif;\">Những nh&oacute;m ng&agrave;nh nghề đ&agrave;o tạo của nh&agrave; trường gồm c&oacute;: C&ocirc;ng nghệ th&ocirc;ng tin, Quản trị kinh doanh, Kế to&aacute;n, Cơ kh&iacute;, Điện &ndash; Điện tử, Ngoại ngữ, Du lịch&hellip; Những nh&oacute;m ng&agrave;nh nghề n&agrave;y vốn l&agrave; sự lựa chọn h&agrave;ng đầu của giới sinh vi&ecirc;n v&agrave; từ đ&oacute; đ&aacute;p ứng nhu cầu doanh nghiệp, nhu cầu x&atilde; hội.&nbsp;</span></p>\r\n\r\n<p style=\"font-size: 13px; line-height: 20.7999992370605px; text-align: center;\"><img src=\"http://tdc.edu.vn/Resources/Images/Article/TDC/Gioithieu/T.png\" style=\"height: 219px; width: 300px;\" /><img src=\"http://tdc.edu.vn/Resources/Images/Article/TDC/Gioithieu/L.png\" style=\"height: 219px; width: 300px;\" /></p>\r\n\r\n<p style=\"font-size: 13px; line-height: 20.7999992370605px; text-align: center;\"><img src=\"http://tdc.edu.vn/Resources/Images/Article/TDC/Gioithieu/J.png\" style=\"height: 219px; width: 300px;\" /><img src=\"http://tdc.edu.vn/Resources/Images/Article/TDC/Gioithieu/O.png\" style=\"height: 219px; width: 300px;\" /></p>\r\n\r\n<p style=\"font-size: 13px; line-height: 20.7999992370605px;\">&nbsp;&nbsp; &nbsp;<span style=\"color: rgb(0, 0, 0); font-family: arial, helvetica, sans-serif;\">Trong qu&aacute; tr&igrave;nh hoạt động, trường Cao đẳng C&ocirc;ng nghệ Thủ Đức đ&atilde; nỗ lực hăng h&aacute;i thi đua dạy tốt, học tốt với phương ch&acirc;m &ldquo;Học đi đ&ocirc;i với h&agrave;nh, l&yacute; luận gắn liền với thực tiễn sản xuất&rdquo;. Trường lu&ocirc;n thực hiện quan điểm ph&aacute;t triển gi&aacute;o dục phải vừa gắn chặt với y&ecirc;u cầu ph&aacute;t triển đất nước vừa ph&ugrave; hợp với xu thế tiến bộ của thời đại, đa dạng h&oacute;a h&igrave;nh thức đ&agrave;o tạo, thực hiện c&ocirc;ng bằng x&atilde; hội trong gi&aacute;o dục.</span></p>\r\n');
