INSERT INTO `default_search_index` VALUES (21,'Home','',NULL,NULL,'pages','pages:page','pages:pages','1','home','admin/pages/edit/1','admin/pages/delete/1'),(2,'Contact','',NULL,NULL,'pages','pages:page','pages:pages','2','contact','admin/pages/edit/2','admin/pages/delete/2'),(3,'Search','',NULL,NULL,'pages','pages:page','pages:pages','3','search','admin/pages/edit/3','admin/pages/delete/3'),(4,'Results','',NULL,NULL,'pages','pages:page','pages:pages','4','search/results','admin/pages/edit/4','admin/pages/delete/4'),(5,'Page missing','',NULL,NULL,'pages','pages:page','pages:pages','5','404','admin/pages/edit/5','admin/pages/delete/5'),(9,'News','',NULL,NULL,'pages','pages:page','pages:pages','9','tin-tuc','admin/pages/edit/9','admin/pages/delete/9'),(12,'News PyroCMS Introduction 1','',NULL,NULL,'pages','pages:page','pages:pages','10','tin-tuc/news-pyrocms-introduction-1','admin/pages/edit/10','admin/pages/delete/10'),(439,'Chương trình triển lãm sách','',NULL,NULL,'pages','pages:page','pages:pages','105','tin-tuc-noi-bat/chuong-trinh-trien-lam-sach','admin/pages/edit/105','admin/pages/delete/105'),(23,'Events','',NULL,NULL,'pages','pages:page','pages:pages','16','su-kien','admin/pages/edit/16','admin/pages/delete/16'),(172,'Nhận thức hình ảnh và nguyên tắc GESTAIL','Trong năm 1910, nh&agrave; t&acirc;m l&yacute; học Max Wertheimer đ&atilde; ph&aacute;t hiện ra điều th&uacute; vị khi &ocirc;ng quan s&aacute;t thấy một loạt c&aacute;c &aacute;nh đ&egrave;n bật tắt khi đo&agrave;n t&agrave;u lửa đi qua một ng&atilde; tư. Điều tương tự với c&aacute;c b&oacute;ng đ&egrave;n nhấp nh&aacute;y được bố tr&iacute; xung quanh rạp chiếu phim.\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\nKhi quan s&aacute;t, ch&uacute;ng ta c&oacute; cảm gi&aacute;c một b&oacute;ng đ&egrave;n đang di chuyển v&ograve;ng v&ograve;ng, trong khi thực tế việc c&aacute;c b&oacute;ng đ&egrave;n cố định nhấp nh&aacute;y c&oacute; thứ tự khiến ch&uacute;ng ta cảm gi&aacute;c n&oacute; đang chạy.\r\n\r\n&nbsp;\r\n\r\nPh&aacute;t hiện n&agrave;y dẫn tới một loạt c&aacute;c nguy&ecirc;n tắc m&ocirc; tả về c&aacute;ch ch&uacute;ng ta nhận biết vật thể. C&aacute;c nguy&ecirc;n tắc n&agrave;y chiếm vị tr&iacute; quan trọng trong mọi vấn đề khi ch&uacute;ng ta l&agrave;m thiết kế.\r\n\r\n&nbsp;\r\n\r\nCh&uacute;ng ta n&ecirc;n bắt đầu với nguy&ecirc;n tắc Gestalt (từ tiếng Đức, nghĩa l&agrave; h&igrave;nh dạng, h&igrave;nh d&aacute;ng), bởi v&igrave;&nbsp;c&aacute;c nguy&ecirc;n tắc thiết kế đều ph&aacute;t sinh từ l&yacute; thuyết gestalt. Trong b&agrave;i n&agrave;y, t&ocirc;i sẽ c&ugrave;ng bạn &ocirc;n lại một ch&uacute;t về l&yacute; thuyết v&agrave; đưa ra một v&agrave;i định nghĩa cơ bản của nguy&ecirc;n tắc Gestalt.\r\n\r\n&nbsp;\r\n\r\nNhững b&agrave;i tới ch&uacute;ng ta sẽ xem x&eacute;t c&aacute;c vấn đề như kh&ocirc;ng gian, c&acirc;n bằng, ph&acirc;n cấp thị gi&aacute;c. Ch&uacute;ng ta sẽ chỉ ra những ảnh hưởng của nguy&ecirc;n tắc Gestalt l&ecirc;n kh&iacute;a cạnh của thiết kế v&agrave; một v&agrave;i v&iacute; dụ thực tế để chỉ ra những nguy&ecirc;n tắc gestalt đang được sử dụng.\r\n\r\n&nbsp;\r\n\r\n&Yacute; tưởng ch&iacute;nh đằng sau của nguy&ecirc;n tắc Gestalt&nbsp;\r\n\r\n\r\n&quot;Tổng thể kh&ocirc;ng phải l&agrave; sự tổng hợp của từng bộ phận&quot;\r\n\r\n&nbsp;\r\n\r\n- Kurt Koffka\r\n\r\n\r\n&nbsp;\r\n\r\nC&acirc;u tr&ecirc;n l&agrave; một nguy&ecirc;n tắc Gestalt thu gọn. Khi con người ch&uacute;ng ta nh&igrave;n thấy một nh&oacute;m vật thể, ch&uacute;ng ta nhận ra to&agrave;n bộ ch&uacute;ng trước khi nh&igrave;n c&aacute;c vật thể ri&ecirc;ng biệt. Ch&uacute;ng ta thấy tổng thể nhiều hơn l&agrave; thấy tất cả vật thể, v&agrave; thậm ch&iacute; khi những phần được chia ra ri&ecirc;ng rẽ, ch&uacute;ng ta vẫn nh&igrave;n theo c&aacute;ch tập hợp ch&uacute;ng như tổng thể.\r\n\r\n&nbsp;\r\n\r\nC&oacute; rất nhiều &yacute; tưởng ch&iacute;nh đằng sau Gestalt:\r\n\r\n&nbsp;\r\n\r\nSự xuất hiện (Tổng thể được x&aacute;c định trước từng phần)\r\n\r\n&nbsp;\r\n\r\nSự xuất hiện l&agrave; qu&aacute; tr&igrave;nh tạo h&igrave;nh c&aacute;c mẫu phức tạp theo c&aacute;c quy luật đơn giản. Khi chuẩn bị x&aacute;c định một đối tượng, đầu ti&ecirc;n ch&uacute;ng ta sẽ x&aacute;c định n&eacute;t b&ecirc;n ngo&agrave;i (outline). Ch&uacute;ng ta sau đ&oacute; định h&igrave;nh c&aacute;c n&eacute;t b&ecirc;n ngo&agrave;i tương ứng với c&aacute;c h&igrave;nh dạng v&agrave; vật thể ch&uacute;ng ta đ&atilde; biết trước đ&oacute; để t&igrave;m h&igrave;nh quen thuộc. Chỉ sau khi nhận ra mẫu ph&ugrave; hợp th&ocirc;ng qua sự tổng hợp c&aacute;c n&eacute;t b&ecirc;n ngo&agrave;i, ch&uacute;ng ta mới bắt đầu x&aacute;c định những phần đ&atilde; tạo n&ecirc;n tổng thể đ&oacute;.&nbsp;\r\n\r\n&nbsp;\r\n\r\nKhi thiết kế, ch&uacute;ng ta cần nhớ rằng con người sẽ nhận ra c&aacute;c vật thể bởi h&igrave;nh d&aacute;ng chung của ch&uacute;ng. Một c&aacute;ch đơn giản để nhận biết vật thể sẽ gi&uacute;p ch&uacute;ng ta xử l&yacute; nhanh hơn l&agrave; đi s&acirc;u v&agrave;o c&aacute;c chi tiết.\r\n\r\n&nbsp;\r\n\r\nSự vật ho&aacute; (bộ &oacute;c sẽ ho&agrave;n th&agrave;nh phần c&ograve;n trống)\r\n\r\n&nbsp;\r\n\r\nSự vật ho&aacute; (reification) l&agrave; một kh&iacute;a cạnh của nhận thức trong đ&oacute; c&aacute;c đối tượng được nhận thức c&oacute; chứa c&aacute;c th&ocirc;ng tin kh&ocirc;ng gian nhiều hơn n&oacute; thật sự c&oacute;.\r\n\r\n&nbsp;\r\n\r\nNhư c&aacute;ch ch&uacute;ng ta cố gắng để khiến thứ ch&uacute;ng ta thấy quen thuộc với thứ n&agrave;o đ&oacute; ch&uacute;ng ta đ&atilde; lưu trong bộ nhớ, m&agrave; thường th&igrave; n&oacute; ch&iacute;nh kh&ocirc;ng ho&agrave;n to&agrave;n ch&iacute;nh x&aacute;c. Thay v&agrave;o đ&oacute; ch&uacute;ng ta t&igrave;m thấy một kết quả gần đ&uacute;ng v&agrave; tự tạo ra c&aacute;c phần c&ograve;n trống m&agrave; ch&uacute;ng ta nghĩ ch&uacute;ng ta sẽ thấy.\r\n\r\n&nbsp;\r\n\r\nSự vật ho&aacute; gi&uacute;p ch&uacute;ng ta kh&ocirc;ng cần phải tr&igrave;nh b&agrave;y một h&igrave;nh dạng b&ecirc;n ngo&agrave;i ho&agrave;n hảo để người xem c&oacute; thể thấy n&oacute;. Ch&uacute;ng ta c&oacute; thể bỏ đi một v&agrave;i phần b&ecirc;n ngo&agrave;i miễn l&agrave; ch&uacute;ng đủ gần gũi với mẫu ban đầu. Bạn c&oacute; thể xem c&aacute;c vị dụ b&ecirc;n dưới đối với quy tắc của sự gắn kết.\r\n\r\n&nbsp;\r\n\r\nLi&ecirc;n kết ổn định (bộ n&atilde;o t&igrave;m c&aacute;ch tr&aacute;nh sự kh&ocirc;ng chắc chắn)\r\n\r\nLi&ecirc;n kết ổn định l&agrave; xu hướng của trải nghiệm cảm nhận mơ hồ nhằm đem lại sự ổn định v&agrave; qua lại giữa c&aacute;c c&aacute;ch diễn giải kh&aacute;c nhau. Một số đối tượng c&oacute; thể được nhận ra theo hơn một c&aacute;ch.&nbsp;Một v&iacute; dụ trong c&aacute;c sắp đặt ở dưới.&nbsp;H&igrave;nh ảnh c&oacute; thể nh&igrave;n thấy như l&agrave; hai khu&ocirc;n mặt hoặc l&agrave; một chiếc b&igrave;nh.\r\n\r\n&nbsp;\r\n\r\nBạn kh&ocirc;ng thể nh&igrave;n thấy cả hai c&ugrave;ng một l&uacute;c. Thay v&agrave;o đ&oacute; bạn chuyển đổi qua lại nhanh ch&oacute;ng giữa hai lựa chọn n&agrave;y. Một c&oacute; vẻ l&agrave; nhận thức chủ yếu v&agrave; sau đ&oacute; bạn bắt đầu c&oacute; thể nh&igrave;n thấy c&aacute;i kia.\r\n\r\n&nbsp;\r\n\r\n\r\nTừ g&oacute;c độ người thiết kế nếu bạn muốn thay đổi nhận thức một số người, đừng c&oacute; cố gắng thay đổi thất cả c&ugrave;ng một l&uacute;c. T&igrave;m một c&aacute;ch n&agrave;o đ&oacute; để khiến họ thấy một sự thay thế b&ecirc;n cạnh yếu tố m&agrave; họ h&igrave;nh dung&nbsp;ban đầu. Sau đ&oacute; tạo th&ecirc;m sức mạnh cho phương ph&aacute;p thay thế đ&oacute; v&agrave; l&agrave;m yếu đi c&aacute;ch ban đầu.\r\n\r\n\r\n&nbsp;\r\n\r\nBất biến (ch&uacute;ng ta giỏi trong việc nhận ra những g&igrave; tương tự v&agrave; kh&aacute;c biệt)\r\n\r\n&nbsp;\r\n\r\nBất biến l&agrave; bản chất của nhận thức trong những đối tượng đơn giản được nhận biết độc lập với những đảo ngược, biến dạng v&agrave; thu ph&oacute;ng. V&igrave; ch&uacute;ng ta thường xuy&ecirc;n gặp phải những đối tượng từ c&aacute;c g&oacute;c độ kh&aacute;c nhau, ch&uacute;ng ta đ&atilde; ph&aacute;t triển khả năng nhận thức ch&uacute;ng cho d&ugrave; ch&uacute;ng xuất hiện kh&aacute;c đi.\r\n\r\n&nbsp;\r\n\r\nH&igrave;nh dung nếu bạn chỉ c&oacute; thể nhận ra ai đ&oacute; bạn biết nếu họ phải đứng ch&iacute;nh diện, v&agrave; bạn kh&ocirc;ng biết nếu họ xoay mặt ngang hoặc quay lưng. Ngo&agrave;i những g&oacute;c độ nhận biết đặc biệt th&igrave; ch&uacute;ng ta vẫn c&oacute; thể nhận ra ai đ&oacute;.\r\n\r\n&nbsp;\r\n\r\nBạn c&oacute; thể thấy những &yacute; tưởng n&agrave;y trong c&aacute;c nguy&ecirc;n tắc dưới. &Yacute; tưởng ch&iacute;nh l&agrave; nguy&ecirc;n tắc Gestalt l&agrave; về nhận thức v&agrave; những g&igrave; được mang tới bởi c&aacute;c đối tượng. C&aacute;c nguy&ecirc;n tắc n&oacute;i ra phần cốt l&otilde;i của ng&ocirc;n ngữ thị gi&aacute;c trong c&aacute;c m&ocirc;i trường l&agrave;m việc.\r\n\r\n&nbsp;\r\n\r\nNguy&ecirc;n tắc Gestalt\r\n\r\nHầu hết c&aacute;c nguy&ecirc;n tắc đều tương đối dễ hiểu. Ch&uacute;ng c&oacute; những nhận biết chung xuất hiện trong đ&oacute;.\r\n\r\n&nbsp;\r\n\r\n\r\n&quot;Tất cả đều tương đương, c&aacute;c yếu tố đều c&oacute; li&ecirc;n quan tới dự định X được nhận thức nh&oacute;m lại th&agrave;nh c&aacute;c đơn vị cấp bậc cao hơn&quot;.\r\n\r\n&nbsp;\r\n\r\n- Stephen Palmer\r\n\r\n\r\n&nbsp;\r\n\r\nRất nhiều c&aacute;c nguy&ecirc;n tắc dưới đ&acirc;y tu&acirc;n theo mẫu n&agrave;y. C&aacute;c nguy&ecirc;n tắc kh&aacute;c x&aacute;c định X hoặc c&aacute;c cấp bậc cao hơn được nhận thấy.\r\n\r\n&nbsp;\r\n\r\nQuy luật của PR&Auml;GNANZ (Tạo h&igrave;nh&nbsp;tốt, quy luật của sự đơn giản)\r\n\r\n&nbsp;\r\n\r\n\r\n&quot;Con người sẽ cảm nhận&nbsp;v&agrave; giải th&iacute;ch h&igrave;nh ảnh mơ hồ hoặc h&igrave;nh ảnh phức tạp trong một/nhiều h&igrave;nh dạng đơn giản nhất c&oacute; thể&quot;.\r\n\r\n\r\n&nbsp;\r\n\r\nĐ&acirc;y l&agrave; nguy&ecirc;n tắc cơ bản của Gestalt. Ch&uacute;ng ta th&iacute;ch những thứ đơn giản, r&otilde; r&agrave;ng v&agrave; được ph&acirc;n cấp. Bản chất của những điều n&agrave;y l&agrave; ch&uacute;ng an to&agrave;n hơn. Ch&uacute;ng lấy &iacute;t thời gian của ch&uacute;ng ta để nhận biết v&agrave; hiển thị &iacute;t bất ngờ hơn.\r\n\r\n&nbsp;\r\n\r\nKhi phải đối mặt với những h&igrave;nh dạng phức tạp, ch&uacute;ng ta c&oacute; xu hướng sắp xếp ch&uacute;ng lại th&agrave;nh c&aacute;c đối tượng đơn giản hoặc một tổng thể đơn giản.&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\nBạn th&iacute;ch nh&igrave;n h&igrave;nh b&ecirc;n tr&aacute;i ph&iacute;a tr&ecirc;n được tạo bởi c&aacute;c v&ograve;ng tr&ograve;n, h&igrave;nh vu&ocirc;ng v&agrave; tam gi&aacute;c đơn giản hơn l&agrave; bạn nh&igrave;n thấy h&igrave;nh b&ecirc;n phải với sự phức tạp m&agrave;u sắc v&agrave; một h&igrave;nh dạng tổng thể mơ hồ.\r\n\r\n&nbsp;\r\n\r\nTrong trường hợp n&agrave;y, nh&igrave;n ba vật thể kh&aacute;c kh&aacute; đơn giản hơn l&agrave; nh&igrave;n một đối tượng phức tạp. Trong trường hợp kh&aacute;c, đơn giản l&agrave; thấy một đối tượng ri&ecirc;ng biệt, điều mang tới ch&uacute;ng ta &hellip; sự gần gũi.\r\n\r\n&nbsp;\r\n\r\nSự li&ecirc;n kết\r\n\r\n&nbsp;\r\n\r\n\r\n&quot;Khi nh&igrave;n một sự sắp xếp c&aacute;c yếu tố phức tạp, ch&uacute;ng ta muốn nh&igrave;n một mẫu đơn v&agrave; c&oacute; thể nhận biết được.\r\n\r\n\r\n&nbsp;\r\n\r\nNhư với quy luật Pr&auml;gnanz, sự li&ecirc;n kết nh&igrave;n đơn giản. Sự li&ecirc;n kết l&agrave; tr&aacute;i ngược với những g&igrave; ta nh&igrave;n thấy trong bức h&igrave;nh quy luật Pr&auml;gnanz ph&iacute;a tr&ecirc;n nơi ba đối tượng đơn giản hơn l&agrave; một. Với sự li&ecirc;n kết, ch&uacute;ng ta thay thế sự kết hợp từng phần v&agrave;o một h&igrave;nh dạng tổng thể đơn giản. Mắt ch&uacute;ng ta tự điền c&aacute;c th&ocirc;ng tin c&ograve;n thiếu để tạo th&agrave;nh những h&igrave;nh h&agrave;i ho&agrave;n hảo.\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\nTrong tấm h&igrave;nh b&ecirc;n tr&aacute;i ph&iacute;a tr&ecirc;n, bạn sẽ thấy một tam gi&aacute;c trắng cho d&ugrave; tr&ecirc;n h&igrave;nh chỉ thực sự gồm ba h&igrave;nh giống như tr&ograve; chơi Pac-Man m&agrave;u đen.\r\n\r\n&nbsp;\r\n\r\nVới h&igrave;nh ph&iacute;a b&ecirc;n phải, bạn thấy một ch&uacute; gấu tr&uacute;c cho d&ugrave; h&igrave;nh hiện hữu l&agrave; tập hợp c&aacute;c h&igrave;nh dạng ngẫu nhi&ecirc;n. Nh&igrave;n ra h&igrave;nh tam gi&aacute;c v&agrave; con gấu dễ hơn l&agrave; cố gắng nh&igrave;n những đối tượng ri&ecirc;ng biệt. Sự li&ecirc;n kết c&oacute; thể coi như l&agrave; một chất keo giữ c&aacute;c yếu tố lại với nhau. Đ&oacute; l&agrave; việc con người c&oacute; xu hướng t&igrave;m kiếm một mẫu quen thuộc.\r\n\r\n&nbsp;\r\n\r\nCh&igrave;a kho&aacute; để gắn kết l&agrave; cung cấp đầy đủ th&ocirc;ng tin để mắt (bộ n&atilde;o) c&oacute; thể tự t&igrave;m ra những phần c&ograve;n lại. Nếu thiếu hụt qu&aacute; nhiều, c&aacute;c yếu tố sẽ sự chia ra c&aacute;c phần nhỏ thay v&igrave; tạo n&ecirc;n một tập hợp. Nếu qu&aacute; nhiều th&ocirc;ng tin đựơc cung cấp, th&igrave; sự gắn kết kh&ocirc;ng xảy ra.\r\n\r\n&nbsp;\r\n\r\nT&iacute;nh đối xứng v&agrave; thứ tự (order)\r\n\r\n&nbsp;\r\n\r\n\r\n&quot;Mọi người c&oacute; xu hướng nhận thức c&aacute;c vật thể với c&aacute;c h&igrave;nh dạng đối xứng từ trung t&acirc;m&quot;.\r\n\r\n\r\n&nbsp;\r\n\r\nSự đối xứng cho ph&eacute;p ch&uacute;ng ta một cảm gi&aacute;c của sự vững chắc v&agrave; trật tự, thứ m&agrave; ch&uacute;ng ta mong muốn thấy.\r\n\r\n&nbsp;\r\n\r\n\r\nCh&uacute;ng ta thường &aacute;p đặt trật tự từ hỗn loạn một c&aacute;ch tự nhi&ecirc;n.\r\n\r\n\r\n&nbsp;\r\n\r\nNguy&ecirc;n tắc n&agrave;y dẫn ta tới mong muốn c&acirc;n bằng trong c&aacute;c sắp xếp th&agrave;nh phần, mặc d&ugrave; c&aacute;c sắp xếp kh&ocirc;ng ho&agrave;n to&agrave;n đối xứng để tạo c&acirc;n bằng.\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\nTrong h&igrave;nh ảnh ph&iacute;a tr&ecirc;n, bạn sẽ thấy 3 cặp dấu ngoặc mở v&agrave; đ&oacute;ng. Nguy&ecirc;n tắc đối xứng khi ch&uacute;ng ta đọc b&agrave;i viết n&agrave;y, c&oacute; thể gợi &yacute; ch&uacute;ng ta thấy c&aacute;i g&igrave; kh&aacute;c. Điều đ&oacute; cho thấy sự đối xứng được ưu ti&ecirc;n hơn l&agrave; gần nhau.',NULL,NULL,'blog','blog:post','blog:posts','3','blog/2014/08/nhn-thc-hinh-nh-va-nguyen-tc-gestail','admin/blog/edit/3','admin/blog/delete/3'),(171,'Thiết kế nhận diện thương hiệu KOODOO','Koodoo l&agrave; thương hiệu b&aacute;n b&aacute;nh pizza nhanh tại Nga. Qua tay những nh&agrave; thiết kế chuy&ecirc;n nghiệp Koodoo c&oacute; một h&igrave;nh ẳn th&acirc;n thiện v&agrave; dễ thương.\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n',NULL,NULL,'blog','blog:post','blog:posts','5','blog/2014/08/thit-k-nhn-din-thng-hiu-koodoo','admin/blog/edit/5','admin/blog/delete/5'),(170,'Top 9 ứng dụng iPad dành cho designer','C&ocirc;ng nghệ ng&agrave;y nay đ&atilde; v&agrave; đang ng&agrave;y c&agrave;ng đẩy l&ugrave;i những c&aacute;ch l&agrave;m việc truyền thống để đem lại sự tiện lợi v&agrave; hiệu quả d&agrave;nh cho giới thiết kế.\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n(Nhấp chuột v&agrave;o h&igrave;nh để tới li&ecirc;n kết tải ứng dụng).\r\n\r\nPaper by FiftyThree\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\nĐ&acirc;y l&agrave; một ứng dụng giải quyết rất tốt việc ph&aacute;c hoạ. N&oacute; c&oacute; giao diện đơn giản, kh&ocirc;ng menu v&agrave; n&uacute;t khiến n&oacute; sử dụng dễ d&agrave;ng. H&atilde;y mang theo iPad v&agrave; bỏ hết giấy b&uacute;t ở nh&agrave;.\r\n\r\n&nbsp;\r\n\r\nAdobe ideas\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\nỨng dụng n&agrave;y sẽ thay thế b&uacute;t v&agrave; giấy của bạn với khung l&agrave;m việc kỹ thuật số đi k&egrave;m với nhiều loại b&uacute;t c&oacute; thẻ tuỳ chỉnh. N&oacute; c&ograve;n c&oacute; ở định dạng vector để bạn sử dụng.\r\n\r\n&nbsp;\r\n\r\nSketchBook Pro for iPad\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\nMột trong những ứng dụng phổ biến nhất cho việc vẽ. N&oacute; c&oacute; rất nhiều b&uacute;t, b&uacute;t ch&igrave;, airbrush để lựa chọn. Ứng dụng n&agrave;y nhẹ v&agrave; dễ sử dụng, n&oacute; cho ph&eacute;p bạn l&agrave;m việc với layer, lựa chọn l&agrave;m mờ, v&agrave; c&aacute;c chế độ blend cao cấp.\r\n\r\n&nbsp;\r\n\r\nAdobe Photoshop Touch\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\nVới ứng dụng n&agrave;y bạn c&oacute; thể &aacute;p dụng c&aacute;c hiệu ứng chuy&ecirc;n nghiệp, hoạt động với nhiều layer v&agrave; c&aacute;c chế độ blend, kết hợp h&igrave;nh ảnh. N&oacute; c&ograve;n li&ecirc;n kết với iClound Photo Stream v&agrave; h&igrave;nh ảnh độ ph&acirc;n giải cao.\r\n\r\n&nbsp;\r\n\r\nTayasui Sketches\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\nỨng dụng n&agrave;y ho&agrave;n hảo cho bất cứ việc g&igrave; m&agrave; ch&uacute;ng ta c&oacute; thể s&aacute;ng tạo. C&oacute; n&oacute; bạn c&oacute; thể ph&aacute;c thảo, t&ocirc; m&agrave;u nước v&agrave; minh hoạ.\r\n\r\n&nbsp;\r\n\r\nInspire Pro\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\nĐem tới một bản ph&aacute;c tuyệt hảo bằng c&aacute;ch sử App n&agrave;y. Ứng dụng sẽ hữu &iacute;ch cho cả d&acirc;n tập sự v&agrave; hoạ sĩ chuy&ecirc;n nghiệp. N&oacute; c&oacute; tới 60 loại b&uacute;t chất lượng cao để lựa chọn, được chia th&agrave;nh 6 bộ; Sơn dầu, airbrush, h&igrave;nh cơ bản, b&uacute;t sắt, phấn m&agrave;u.\r\n\r\n&nbsp;\r\n\r\nAuryn Ink\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\nBạn đang c&oacute; trong tay một ứng dụng c&oacute; thể vẽ m&agrave;u nước v&ocirc; c&ugrave;ng dễ d&agrave;ng. N&oacute; c&oacute; những t&iacute;nh năng giới hạn c&oacute; thể so s&aacute;nh với những ứng dụng c&oacute; trong danh s&aacute;ch.\r\n\r\n&nbsp;\r\n\r\nArtRage\r\n\r\n\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\nỨng dụng c&oacute; rất nhiều lựa chọn về giấy, đi k&egrave;m với nhiều loại b&uacute;t: b&uacute;t ch&igrave;, phấn, b&uacute;t l&ocirc;ng&hellip;\r\n\r\n&nbsp;\r\n\r\nZen Brush\r\n\r\n​\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\nVới 64 mẫu giấy v&agrave; 3 loại b&uacute;t c&oacute; sẵ. bạn c&oacute; thể thử sức với b&uacute;t mực, rất dễ sử dụng.\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\nTheo WDL',NULL,NULL,'blog','blog:post','blog:posts','6','blog/2014/08/top-9-ng-dng-ipad-danh-cho-designer','admin/blog/edit/6','admin/blog/delete/6'),(144,'Ứng đối trôi chảy','Giữ c&aacute;i đầu lạnh trước &aacute;p lực\r\n\r\n&ldquo;&Agrave; Susan, b&aacute;o c&aacute;o của c&ocirc; chỉ ra rằng c&ocirc; đang nỗ lực để mở rộng nhưng liệu c&ocirc; c&oacute; c&acirc;n nhắc được những t&aacute;c động c&oacute; thể xảy đến cho kh&aacute;ch h&agrave;ng của ch&uacute;ng ta kh&ocirc;ng? C&ocirc; chưa qu&ecirc;n thất bại ở Dallas năm ngo&aacute;i chứ, họ cũng triển khai một dự &aacute;n tương tự như vậy đấy&rdquo;\r\n\r\nV&acirc;ng, nếu bạn l&agrave; Susan, hẳn bạn đang cảm thấy đối mặt với một &aacute;p lực thực sự đấy. Tr&aacute;ch nhiệm của bạn l&agrave; phải trả lời chất vấn đồng thời xoa dịu đi nỗi lo lắng của ng&agrave;i CEO về t&igrave;nh trạng xấu xảy đến với kh&aacute;ch h&agrave;ng. Bạn sẽ l&agrave;m g&igrave;? Bạn sẽ n&oacute;i g&igrave;? Bạn sẽ n&oacute;i như thế n&agrave;o? V&agrave; sẽ ra sao nếu bạn chẳng nghĩ ra được điều g&igrave; để n&oacute;i ?\r\n\r\nTrường hợp n&agrave;y kh&aacute; phổ biến. Cho d&ugrave; bạn đang ở bất cứ đ&acirc;u: trong một cuộc họp, tr&igrave;nh b&agrave;y một đề xuất, diễn thuyết một &yacute; tưởng hoặc trả lời c&acirc;u hỏi sau buổi thuyết tr&igrave;nh, t&agrave;i ứng đối tr&ocirc;i chảy l&agrave; một kỹ năng cần thiết. Một kỹ năng tuyệt vời m&agrave; nếu bạn th&agrave;nh thạo, những c&acirc;u trả lời sắc sảo của bạn sẽ ngay lập tức tăng t&iacute;nh thuyết phục cho điều bạn n&oacute;i.\r\n\r\nKhi bạn c&oacute; thể nhanh ch&oacute;ng chuyển đổi những &yacute; tưởng v&agrave; suy nghĩ của m&igrave;nh th&agrave;nh một đ&aacute;p &aacute;n mạch lạc, chắc chắn n&oacute; sẽ được tiếp thu. Hơn nữa, bạn sẽ x&acirc;y dựng cho m&igrave;nh một phong th&aacute;i tự tin, thuyết phục v&agrave; đ&aacute;ng tin cậy trong mắt người kh&aacute;c.\r\n\r\nSự tự tin l&agrave; ch&igrave;a kh&oacute;a của qu&aacute; tr&igrave;nh r&egrave;n luyện kỹ năng ứng đối tr&ocirc;i chảy. Khi bạn tr&igrave;nh b&agrave;y một th&ocirc;ng tin, n&ecirc;u ra một &yacute; kiến hay đưa ra một đề xuất, phải chắc rằng bạn biết m&igrave;nh đang n&oacute;i g&igrave; cũng như những g&igrave; bạn n&oacute;i được người kh&aacute;c tiếp nhận. Điều n&agrave;y kh&ocirc;ng đồng nghĩa với việc bạn phải biết tất tần tật về mọi thứ, nhưng nếu bạn tự tin về kiến thức trong l&atilde;nh vực đ&oacute; th&igrave; sự tự tin n&agrave;y sẽ gi&uacute;p bạn duy tr&igrave; vẻ điềm tĩnh v&agrave; tự chủ ngay cả khi bạn bất ngờ rơi v&agrave;o t&igrave;nh huống kh&oacute; khăn.\r\n\r\nHọc c&aacute;ch ứng đối tr&ocirc;i chảy\r\n\r\nB&iacute; mật của &ldquo;ph&eacute;p m&agrave;u&rdquo; ứng đối tr&ocirc;i chảy đ&atilde; sẵn s&agrave;ng được bật m&iacute;: trau dồi th&ecirc;m một số kỹ năng v&agrave; s&aacute;ch lược, v&agrave; tr&igrave;nh b&agrave;y một v&agrave;i ho&agrave;n cảnh dễ khiến bạn gặp phải &aacute;p lực. Sau đ&oacute;, khi bạn phải đối mặt với những c&acirc;u hỏi hay những cuộc tranh luận bất đắc dĩ th&igrave; bạn đ&atilde; được chuẩn bị sẵn s&agrave;ng. H&atilde;y cố gắng tỏ ra điềm tĩnh, sắp xếp lại suy nghĩ trước khi đưa ra c&acirc;u trả lời cuối c&ugrave;ng. Dưới đ&acirc;y l&agrave; một v&agrave;i gợi &yacute; v&agrave; s&aacute;ch lược:\r\n\r\n1-&nbsp;Thư gi&atilde;n:\r\n\r\nNghe c&oacute; vẻ m&acirc;u thuẫn, l&agrave;m sao m&agrave; thư gi&atilde;n được khi bạn đang rơi v&agrave;o t&igrave;nh huống ng&agrave;n c&acirc;n treo sợi t&oacute;c? Nhưng để giữ cho giọng n&oacute;i vẫn điềm tĩnh cũng như để bộ n&atilde;o l&agrave;m việc, bạn phải thư gi&atilde;n hết mức c&oacute; thể.\r\n\r\n-H&iacute;t thở thật s&acirc;u.\r\n\r\n-Cho cơ thể 1 gi&acirc;y để tiếp nhận những th&ocirc;ng điệp t&iacute;ch cực.\r\n\r\n-Siết chặt cơ bắp ở đ&ugrave;i, bắp tay, b&agrave;n ch&acirc;n v&agrave;i gi&acirc;y sau đ&oacute; thả lỏng ra.\r\n\r\n&nbsp;\r\n\r\n2-&nbsp;Lắng nghe:\r\n\r\nKh&ocirc;ng c&oacute; g&igrave; phải ngạc nhi&ecirc;n khi lắng nghe đ&oacute;ng một vai tr&ograve; quan trọng. Vậy tại sao bạn cần phải lắng nghe? L&agrave; bởi v&igrave; bạn cần phải ho&agrave;n to&agrave;n hiểu được c&acirc;u hỏi hay lời đề nghị trước khi đ&aacute;p lại. Trả lời qu&aacute; nhanh dễ khiến bạn bị hớ. N&ecirc;n nắm được 3 điều:\r\n\r\n-Nh&igrave;n thẳng v&agrave;o người hỏi.\r\n\r\n-Để &yacute; quan s&aacute;t ng&ocirc;n ngữ cơ thể người đ&oacute;, cũng như những g&igrave; được n&oacute;i.\r\n\r\n-Cố gắng diễn dịch th&aacute;i độ trong c&acirc;u hỏi người đ&oacute;. Đ&oacute; l&agrave; một sự c&ocirc;ng k&iacute;ch, một đề nghị ch&iacute;nh đ&aacute;ng hay đơn giản l&agrave; để thử bạn. Tại sao người đ&oacute; lại hỏi như vậy v&agrave; mục đ&iacute;ch của họ l&agrave; g&igrave; ?\r\n\r\n\r\nLời khuy&ecirc;n:\r\n\r\nBạn n&ecirc;n nhớ rằng chỉ khi quan t&acirc;m th&igrave; người ta mới hỏi. Đ&oacute; c&oacute; thể l&agrave; thiện &yacute; &ndash; người ta chỉ đơn giản l&agrave; muốn biết th&ecirc;m, hoặc &aacute;c &yacute; &ndash; muốn l&agrave;m cho bạn kh&oacute; xử. Cho d&ugrave; &yacute; định của họ thế n&agrave;o th&igrave; họ vẫn quan t&acirc;m đến những điều bạn n&oacute;i ra. Cố gắng để đừng l&agrave;m phật &yacute; họ.\r\n\r\n\r\n3-&nbsp;Lặp lại c&acirc;u hỏi:\r\n\r\nNếu bạn thực sự cảm thấy căng thẳng, h&atilde;y đề nghị họ nhắc lại c&acirc;u hỏi. N&oacute; gi&uacute;p bạn c&acirc;u th&ecirc;m ch&uacute;t thời gian trong l&uacute;c suy nghĩ về lời giải đ&aacute;p.\r\n\r\nThoạt nh&igrave;n th&igrave; nhiều người sẽ nghĩ rằng điều đ&oacute; chỉ l&agrave;m cho h&igrave;nh ảnh của họ thật thiếu tự tin, nhưng thực tế lại kh&ocirc;ng phải vậy. N&oacute; khiến cho họ thấy bạn thật sự quan t&acirc;m tới c&acirc;u hỏi để đưa ra c&acirc;u trả lời hay nhất. N&oacute; cũng gi&uacute;p người hỏi c&oacute; cơ hội để diễn đạt lại &yacute; của họ dễ hiểu hơn. Đừng qu&ecirc;n một điều, người hỏi cũng c&oacute; thể chỉ buột miệng hỏi vậy chứ họ chưa thực sự chuẩn bị kỹ cho c&acirc;u hỏi, bằng c&aacute;ch n&agrave;y, bạn cho họ một cơ hội để tr&igrave;nh b&agrave;y lại c&acirc;u hỏi cho thật r&otilde; r&agrave;ng.\r\n\r\nTh&ocirc;ng qua việc đề nghị nhắc lại c&acirc;u hỏi, bạn nắm th&ecirc;m một cơ hội để nhận biết &yacute; định của người hỏi. Nếu c&acirc;u hỏi được sắp xếp lại cụ thể v&agrave; dễ nghe hơn, th&igrave; thật sự họ muốn biết nhiều hơn nữa. C&ograve;n nếu c&acirc;u hỏi được đưa ra c&oacute; vẻ c&ocirc;ng k&iacute;ch hơn ban đầu, bạn n&ecirc;n hiểu rằng đối tượng đang muốn l&agrave;m kh&oacute; bạn. Bước thứ 4 sẽ c&oacute; &iacute;ch cho bạn Khi gặp phải trường hợp n&agrave;y.\r\n\r\n4- Sử dụng phương &aacute;n c&acirc;u giờ.\r\n\r\nĐ&ocirc;i khi bạn cần th&ecirc;m thời gian để ổn định suy nghĩ v&agrave; xoa dịu bản th&acirc;n để đưa ra c&acirc;u trả lời r&otilde; r&agrave;ng. Điều cuối c&ugrave;ng m&agrave; bạn muốn l&agrave;m l&agrave; n&oacute;i ra điều đầu ti&ecirc;n m&igrave;nh nghĩ.\r\n\r\n-H&atilde;y tự m&igrave;nh nhắc lại c&acirc;u hỏi. N&oacute; cho bạn th&ecirc;m thời gian để nghĩ v&agrave; l&agrave;m r&otilde; điều người ta hỏi. N&oacute; cũng cho ph&eacute;p bạn diễn đạt lại nếu cần v&agrave; l&agrave;m thay đổi đ&ograve;i hỏi của người kh&aacute;c theo hướng t&iacute;ch cực. &ldquo;Anh hỏi rằng t&ocirc;i đ&atilde; t&iacute;nh to&aacute;n đến t&aacute;c động l&ecirc;n kh&aacute;ch h&agrave;ng như thế n&agrave;o để từ đ&oacute; chắc chắn rằng họ vẫn c&oacute; được những trải nghiệm t&iacute;ch cực khi ch&uacute;ng ta tiến h&agrave;nh mở rộng phải kh&ocirc;ng?&rdquo;\r\n\r\n-Thu hẹp sự tập trung. Ở đ&acirc;y bạn sẽ hỏi một c&acirc;u, kh&ocirc;ng chỉ để r&otilde; r&agrave;ng cho bạn m&agrave; c&ograve;n l&aacute;i c&acirc;u hỏi sang một hướng dễ kể so&aacute;t hơn. &ldquo;C&oacute; lẽ anh đang muốn nghe quan điểm của t&ocirc;i về t&aacute;c động l&ecirc;n kh&aacute;ch h&agrave;ng chứ g&igrave;. Vậy th&igrave; t&aacute;c động n&agrave;o anh quan t&acirc;m nhất: số sản phẩm b&aacute;n được hay dịch vụ b&aacute;n h&agrave;ng?&rdquo;\r\n\r\n-Y&ecirc;u cầu người kh&aacute;c l&agrave;m r&otilde; c&acirc;u hỏi. Một lần nữa, điều n&agrave;y sẽ buộc người đ&oacute; phải hỏi cụ thể hơn v&agrave; hy vọng rằng c&acirc;u hỏi c&agrave;ng gần đến một điểm cụ thể. &ldquo;Khi anh n&oacute;i anh muốn biết t&ocirc;i ph&acirc;n t&iacute;ch điều đ&oacute; thế n&agrave;o, c&oacute; phải anh muốn t&ocirc;i đưa ra một bảng ph&acirc;n t&iacute;ch chi tiết hoặc phương ph&aacute;p ph&acirc;n t&iacute;ch của t&ocirc;i phải kh&ocirc;ng?&rdquo;\r\n\r\n-Hỏi về c&aacute;c định nghĩa. Những từ chuy&ecirc;n ng&agrave;nh c&oacute; thể l&agrave;m kh&oacute; bạn. Đừng ngại hỏi nghĩa của ch&uacute;ng để chắc rằng điều bạn n&oacute;i kh&ocirc;ng bị lạc đề.\r\n\r\n5-&nbsp; Im lặng l&agrave; v&agrave;ng:\r\n\r\nCh&uacute;ng ta bẩm sinh đ&atilde; cho rằng sự im lặng l&agrave; kh&ocirc;ng thoải m&aacute;i. Tuy nhi&ecirc;n nếu ch&uacute;ng ta d&ugrave;ng n&oacute; đ&uacute;ng liều đ&uacute;ng lượng, n&oacute; lại cho ch&uacute;ng ta thấy khả năng kiểm so&aacute;t được suy nghĩ của m&igrave;nh cũng như tự tin v&agrave;o khả năng đối đ&aacute;p như nước chảy của bản th&acirc;n. Trả lời qu&aacute; nhanh sẽ dễ g&acirc;y ra chuyện &ldquo;c&aacute;i miệng hại c&aacute;i th&acirc;n&rdquo;.&nbsp; H&atilde;y tạm ngừng để tổng hợp c&aacute;c suy nghĩ lại cho thật chậm r&atilde;i v&agrave; cẩn thận nh&eacute;.\r\n\r\n6- B&aacute;m v&agrave;o một điểm v&agrave; một mẩu th&ocirc;ng tin hỗ trợ:\r\n\r\nĐ&ocirc;i khi do qu&aacute; căng thẳng m&agrave; bạn phun ra qu&aacute; &iacute;t hay qu&aacute; nhiều th&ocirc;ng tin. Nếu trả lời qu&aacute; ngắn gọn, bạn sẽ dễ biến cuộc đối thoại th&agrave;nh một cuộc thẩm vấn (Bạn sẽ bị hỏi th&ecirc;m lần nữa v&agrave; người hỏi sẽ nắm đằng chu&ocirc;i bạn). C&ograve;n nếu trả lời qu&aacute; rườm r&agrave;, bạn sẽ đ&aacute;nh mất sự quan t&acirc;m của họ, l&agrave;m cho họ thấy ch&aacute;n v&agrave; ph&aacute;t gi&aacute;c những điều m&agrave; lẽ ra bạn kh&ocirc;ng n&ecirc;n n&oacute;i. N&ecirc;n nhớ rằng họ kh&ocirc;ng y&ecirc;u cầu bạn phải đọc nguy&ecirc;n một b&agrave;i diễn văn về đề t&agrave;i đ&oacute;. Họ chỉ muốn biết một v&agrave;i thứ m&agrave; th&ocirc;i. H&atilde;y t&ocirc;n trọng điều đ&oacute; v&agrave; cho họ c&acirc;u trả lời với đầy đủ th&ocirc;ng tin cần thiết.\r\n\r\nPhương ph&aacute;p n&agrave;y đem đến cho bạn sự tập trung.&nbsp; Thay v&igrave; cố gắng nhồi tất cả những &yacute; tưởng c&oacute; trong đầu bạn lại một cục, bạn chỉ cần chọn một điểm ch&iacute;nh v&agrave; một sự thật để hỗ trợ điểm ch&iacute;nh đ&oacute;. Bằng c&aacute;ch n&agrave;y, bạn c&oacute; thể trả lời c&acirc;u hỏi một c&aacute;ch ch&iacute;nh x&aacute;c v&agrave; đầy chắc chắn.\r\n\r\n\r\nLời khuy&ecirc;n :\r\n\r\nH&atilde;y cho họ biết nếu bạn thật sự kh&ocirc;ng biết c&acirc;u trả lời. Đừng cố gắng qu&aacute; sức, n&oacute; chỉ khiến cho bạn tr&ocirc;ng thật khờ khạo, đồng thời cũng l&agrave;m giảm sự tự tin của bạn nếu gặp phải một t&igrave;nh huống tương tự trong tương lai. Chẳng c&oacute; g&igrave; sai tr&aacute;i cả nếu bạn kh&ocirc;ng biết về một thứ g&igrave; đ&oacute;. Đơn giản l&agrave; trả lời ngay khi c&oacute; thể sau khi bạn đ&atilde; t&igrave;m thấy c&acirc;u trả lời.\r\n\r\n\r\n7- Đo&aacute;n biết:\r\n\r\nTh&ocirc;ng thường, bằng một ch&uacute;t suy luận bạn c&oacute; thể đo&aacute;n ra loại c&acirc;u hỏi tiếp theo m&igrave;nh sắp đối mặt l&agrave; g&igrave; để chuẩn bị sẵn s&agrave;ng đối ph&oacute; với n&oacute;. VD: Bạn chuẩn bị phải tường tr&igrave;nh về doanh số b&aacute;n ra h&agrave;ng th&aacute;ng cho đội quản l&yacute; của bạn. Bản b&aacute;o c&aacute;o của bạn c&oacute; thể giải quyết phần lớn những c&acirc;u hỏi m&agrave; họ sẽ đưa ra, nhưng liệu bạn c&oacute; thể đo&aacute;n biết những c&acirc;u hỏi kh&aacute;c được kh&ocirc;ng? Th&aacute;ng n&agrave;y c&oacute; g&igrave; kh&aacute;c so với những th&aacute;ng kia? C&acirc;u hỏi mới sẽ l&agrave; g&igrave;? Trả lời ra sao? Bạn sẽ cần th&ecirc;m th&ocirc;ng tin g&igrave; để hỗ trợ cho những c&acirc;u hỏi chi tiết?\r\n\r\nĐặc biệt bạn cần d&agrave;nh thời gian động n&atilde;o v&agrave; t&igrave;m lời giải đ&aacute;p&nbsp; cho những c&acirc;u hỏi c&acirc;n n&atilde;o người ta c&oacute; thể đưa ra.\r\n\r\n8- Tập ăn n&oacute;i tr&ocirc;i chảy:\r\n\r\nC&aacute;ch m&agrave; bạn n&oacute;i cũng quan trọng kh&ocirc;ng k&eacute;m g&igrave; điều bạn n&oacute;i. Liệu bạn c&oacute; đủ sức thuyết phục người kh&aacute;c kh&ocirc;ng nếu cứ chốc chốc lại &ldquo;&agrave;&rdquo; &ldquo;ừm&rdquo;. Nắm được những điểm then chốt n&agrave;y khi n&oacute;i chuyện với người kh&aacute;c:\r\n\r\n-N&oacute;i năng mạnh mẽ (Mạnh mẽ chứ kh&ocirc;ng ồn &agrave;o nh&eacute;)\r\n\r\n-Biết tạm dừng đ&uacute;ng l&uacute;c để nhấn mạnh điểm quan trọng hoặc &ldquo;hạ nhiệt&rdquo; cho bản th&acirc;n.\r\n\r\n-Thay đổi giọng n&oacute;i cho ph&ugrave; hợp với th&ocirc;ng điệp bạn gửi.\r\n\r\n-Giao tiếp bằng mắt một c&aacute;ch th&iacute;ch hợp.\r\n\r\n-Cẩn thận ngữ ph&aacute;p.\r\n\r\n-Sử dụng mức độ trang trọng ph&ugrave; hợp với ho&agrave;n cảnh n&oacute;i chuyện.\r\n\r\n9- T&oacute;m tắt v&agrave; kết th&uacute;c:\r\n\r\nTổng kết những điều bạn n&oacute;i bằng một b&agrave;i t&oacute;m tắt nhanh gọn. Sau đ&oacute;, đừng th&ecirc;m bớt bất cứ th&ocirc;ng tin g&igrave; v&agrave; h&atilde;y im lặng. Đừng cố phạm một lỗi hay gặp l&agrave; n&oacute;i nhiều để khỏa lấp sự im lặng. Đ&acirc;y l&agrave; l&uacute;c người ta đang tiếp thu c&acirc;u trả lời của bạn. Nếu bạn cố gắng nhồi nh&eacute;t th&ecirc;m th&ocirc;ng tin sẽ khiến họ bị loạn v&agrave; c&ocirc;ng sức n&atilde;y giờ của bạn tan th&agrave;nh m&acirc;y kh&oacute;i.\r\n\r\nSử dụng một v&agrave;i từ để chỉ ra l&agrave; bạn đang t&oacute;m tắt (v&iacute; dụ: kết luận, cuối c&ugrave;ng&hellip;) hoặc nhắc lại ngắn gọn c&acirc;u hỏi v&agrave; c&acirc;u trả lời. VD: &ldquo;Anh thắc mắc về việc t&ocirc;i đ&atilde; l&agrave;m g&igrave; để ph&acirc;n t&iacute;ch những t&aacute;c động l&ecirc;n kh&aacute;ch h&agrave;ng phải kh&ocirc;ng? T&ocirc;i xem x&eacute;t kỹ lưỡng trường hợp của Dallas v&agrave; đ&atilde; chuẩn bị một bản ph&acirc;n t&iacute;ch &ldquo;C&aacute;i g&igrave; sẽ đến&rdquo; cho ri&ecirc;ng c&ocirc;ng ty ta&rdquo;\r\n\r\nĐiểm cốt l&otilde;i:\r\n\r\nChẳng ai th&iacute;ch bị hỏi bất ngờ cả. Kh&ocirc;ng chắc chắn về c&acirc;u trả lời c&oacute; thể g&acirc;y căng thẳng cho bạn. Tuy nhi&ecirc;n sự căng thẳng đ&oacute; vẫn nằm trong sự quản l&yacute; của bạn nếu bạn nắm vững những g&igrave; ch&uacute;ng ta đ&atilde; thảo luận n&atilde;y giờ. Về cơ bản, ứng đ&aacute;p tr&ocirc;i chảy nghĩa l&agrave; kiểm so&aacute;t được t&igrave;nh h&igrave;nh. Bằng c&aacute;ch hỏi, c&acirc;u th&ecirc;m thời gian, v&agrave; b&aacute;m v&agrave;o một điểm để trả lời. Khi bạn đ&atilde; nh&igrave;n r&otilde; được vấn đề họ quan t&acirc;m, bạn c&oacute; thể trả lời rất tr&ocirc;i chảy v&agrave; quan trọng hơn nữa, điều đ&oacute; sẽ gi&uacute;p bạn g&acirc;y ấn tượng với người hỏi bằng phong th&aacute;i tự tin&nbsp; v&agrave; tự chủ của m&igrave;nh.',NULL,NULL,'blog','blog:post','blog:posts','8','blog/2014/08/ung-doi-troi-chay','admin/blog/edit/8','admin/blog/delete/8'),(142,'Bản đồ tư duy','C&aacute;c từ kh&oacute;a li&ecirc;n quan: Sơ đồ nh&aacute;nh, Sơ đồ mạng nhện, sơ đồ tư duy&hellip;\r\n\r\nBản đồ tư duy (Mind Map&trade; )&nbsp; l&agrave; 1 thương hiệu của tổ chức Buzan.\r\n\r\nBản đồ tư duy l&agrave; một kỹ thuật hữu hiệu để cải thiện phương ph&aacute;p ghi ch&eacute;p của bạn, hỗ trợ v&agrave; tăng cường sự s&aacute;ng tạo của bạn trong c&aacute;ch giải quyết một vấn đề.\r\n\r\nBằng c&aacute;ch sử dụng Bản đồ tư duy, bạn c&oacute; thể nhanh ch&oacute;ng nhận biết v&agrave; hiểu r&otilde; cấu tr&uacute;c của một chủ đề, nắm r&otilde; những mảng th&ocirc;ng tin, dữ liệu đ&oacute; li&ecirc;n kết với nhau như thế n&agrave;o, cũng như việc ghi những dữ liệu sơ cấp trong phương ph&aacute;p ghi ch&eacute;p b&igrave;nh thường.\r\n\r\nNhưng, hơn thế nữa, Bản đồ tư duy c&ograve;n ph&aacute;t triển khả năng s&aacute;ng tạo của bạn khi giải quyết một vấn đề, sắp xếp th&ocirc;ng tin một c&aacute;ch hợp l&yacute; gi&uacute;p bạn dễ nhớ v&agrave; dễ đọc lại.\r\n\r\nBản đồ tư duy của Tony Buzan đ&atilde; thực sự giết chết c&aacute;c phương ph&aacute;p ghi ch&eacute;p b&igrave;nh thường. N&oacute; tạo ra một cấu tr&uacute;c hai chiều. Một bản đồ tư duy tốt chỉ ra được &ldquo;h&igrave;nh d&aacute;ng&rdquo; của chủ thể, tầm quan trọng của việc li&ecirc;n kết những chi tiết rời rạc, v&agrave; ch&uacute;ng li&ecirc;n quan với nhau như thế n&agrave;o.\r\n\r\nBản đồ tư duy tinh gọn hơn so với những ghi ch&eacute;p th&ocirc;ng thường, thường chỉ tr&ecirc;n một mặt giấy. Điều n&agrave;y gi&uacute;p bạn li&ecirc;n kết c&aacute;c dữ kiện dễ d&agrave;ng hơn. V&agrave; sau khi đ&atilde; vẽ phần ch&iacute;nh của sơ đồ tư duy nếu t&igrave;m được th&ecirc;m th&ocirc;ng tin kh&aacute;c th&igrave; bạn vẫn c&oacute; thể bổ sung v&agrave;o một c&aacute;ch dễ d&agrave;ng.\r\n\r\nBản đồ tư duy rất hữu &iacute;ch cho:\r\n\r\n\r\n	T&oacute;m tắt th&ocirc;ng tin.\r\n	Tổng hợp th&ocirc;ng tin từ c&aacute;c nguồn nghi&ecirc;n cứu kh&aacute;c nhau.\r\n	Suy nghĩ th&ocirc;ng qua những vấn đề phức tạp.\r\n	Tr&igrave;nh b&agrave;y th&ocirc;ng tin theo một định dạng m&agrave; n&oacute; thể hiện được tổng quan cấu tr&uacute;c của vấn đề.\r\n\r\n\r\nHơn nữa, việc đọc lại cũng sẽ rất nhanh, khi bạn cần nhớ lại c&aacute;c th&ocirc;ng tin bạn chỉ cần liếc mắt sơ qua. V&agrave; tr&iacute; nhớ của bạn cũng hoạt động hiệu quả hơn. Bằng việc ghi nhớ h&igrave;nh dạng v&agrave; cấu tr&uacute;c của bản đồ tư duy, n&oacute; cũng c&oacute; thể gi&uacute;p bạn gợi nhớ c&aacute;c th&ocirc;ng tin. Như vậy, n&oacute; đ&atilde; l&agrave;m cho bộ n&atilde;o của bạn hoạt động nhiều hơn so với c&aacute;ch ghi ch&eacute;p th&ocirc;ng thường trong tiến tr&igrave;nh tiếp thu v&agrave; kết nối c&aacute;c dữ kiện.\r\n\r\nCải tiến bản đồ tư duy của bạn\r\n\r\nMột khi bạn đ&atilde; hiểu c&aacute;ch vẽ bản đồ tư duy th&igrave; bạn c&oacute; thể tự quy ước c&aacute;ch vẽ của ri&ecirc;ng m&igrave;nh. Những gợi &yacute; sau đ&acirc;y sẽ gi&uacute;p bạn tăng hiệu quả cho bản đồ tư duy của m&igrave;nh:\r\n\r\n\r\n	Sử dụng những từ ngắn gọn hay c&aacute;c cụm từ đơn giản: Hầu hết những từ trong c&aacute;ch ghi ch&eacute;p b&igrave;nh thường chỉ l&agrave; để đệm cho &yacute; ch&iacute;nh : ch&uacute;ng truyền đạt những dữ kiện, th&ocirc;ng tin trong một bối cảnh nhất định v&agrave; l&agrave;m cho dễ đọc hơn. Nhưng trong bản đồ tư duy, những từ ngắn gọn, bắt mắt v&agrave; những cụm từ đầy đủ &yacute; nghĩa cũng c&oacute; thể chuyển tải những nội dung tương tự một c&aacute;ch hiệu quả. Những từ thừa chỉ l&agrave;m cho bản đồ th&ecirc;m rối hơn.\r\n	Viết theo lối chữ in hoặc c&oacute; thể in: v&igrave; những chữ viết tay kh&ocirc;ng r&otilde; r&agrave;ng ,nghệch ngoặc hoặc nối với nhau lộn xộn chỉ l&agrave;m bạn kh&oacute; đọc hơn.\r\n	Sử dụng m&agrave;u sắc để ph&acirc;n biệt c&aacute;c &yacute; tưởng kh&aacute;c nhau: N&oacute; gi&uacute;p bạn ph&acirc;n loại c&aacute;c &yacute; tưởng tốt hơn, dễ nhớ hơn v&agrave; cũng&nbsp; dễ sắp xếp hơn.\r\n	Sử dụng biểu tượng v&agrave; h&igrave;nh ảnh: Nếu một biểu tượng hay h&igrave;nh ảnh n&agrave;o đ&oacute; c&oacute; &yacute; nghĩa với bạn th&igrave; h&atilde;y sử dụng n&oacute; v&igrave; h&igrave;nh ảnh th&igrave; sẽ dễ nhớ hơn l&agrave; chữ viết.\r\n	Sử dụng c&aacute;c li&ecirc;n kết ch&eacute;o: th&ocirc;ng tin trong một phần của bản đồ tư duy c&oacute; thể li&ecirc;n quan đến c&aacute;c phần kh&aacute;c. Bạn c&oacute; thể vẽ c&aacute;c đường nối ch&uacute;ng lại nhằm thể hiện sự li&ecirc;n quan đ&oacute;. Điều n&agrave;y sẽ gi&uacute;p bạn thấy được sự li&ecirc;n kết giữa c&aacute;c phần kh&aacute;c nhau trong chủ đề.\r\n\r\n\r\nV&iacute; dụ bản đồ tư duy:\r\n\r\n\r\n\r\nBản đồ tư duy\r\n\r\n\r\n&nbsp;\r\n\r\nĐiểm cốt l&otilde;i:\r\n\r\nBản đồ tư duy l&agrave; một phương ph&aacute;p ghi ch&eacute;p cực k&igrave; hiệu quả.&nbsp; N&oacute; kh&ocirc;ng chỉ m&ocirc; tả c&aacute;c dữ kiện m&agrave; c&ograve;n m&ocirc; tả cả cấu tr&uacute;c tổng thể của chủ đề v&agrave; tầm quan trọng của việc li&ecirc;n kết những chi tiết rời rạc trong đ&oacute;. N&oacute; gi&uacute;p bạn kết nối v&agrave; tạo ra mối li&ecirc;n hệ giữa c&aacute;c &yacute; tưởng, đ&acirc;y l&agrave; điều m&agrave; bạn kh&oacute; c&oacute; thể l&agrave;m được ở c&aacute;ch ghi ch&eacute;p th&ocirc;ng thường.\r\n\r\nNếu bạn nghi&ecirc;n cứu hay ghi ch&eacute;p về 1 vấn đề n&agrave;o đ&oacute;, h&atilde;y thử vẽ bằng Bản đồ tư duy, chắc chắn bạn sẽ thấy n&oacute; hữu dụng một c&aacute;ch đ&aacute;ng kinh ngạc.\r\n\r\nTrong b&agrave;i viết thiếp theo, ch&uacute;ng ta sẽ t&igrave;m hiểu về&nbsp;SQ3R, l&agrave; một kĩ thuật gi&uacute;p bạn tiếp thu th&ocirc;ng tin văn bản nhiều nhất. Để đọc, h&atilde;y nhấp chuột v&agrave;o phần &ldquo;Next article&rdquo; b&ecirc;n dưới. Những phần c&oacute; li&ecirc;n quan được đăng trong phần &ldquo;T&agrave;i nguy&ecirc;n mở rộng&rdquo; được liệt k&ecirc; b&ecirc;n dưới.',NULL,NULL,'blog','blog:post','blog:posts','9','blog/2014/08/ban-do-tu-duy','admin/blog/edit/9','admin/blog/delete/9'),(143,'Kỹ năng giao tiếp','Ph&aacute;t triển kỹ năng giao tiếp của bạn\r\n\r\nGiải m&atilde; c&aacute;ch giao tiếp hiệu quả. Hiểu v&agrave; được hiểu.\r\n\r\nD&ugrave; l&agrave; sinh vi&ecirc;n, đang l&agrave;m việc cho một tập đo&agrave;n lớn, một c&ocirc;ng ty cỡ nhỏ, hay thậm ch&iacute; chỉ kinh doanh tại nh&agrave;&ndash; bạn vẫn cần giao tiếp hiệu quả để đạt được th&agrave;nh c&ocirc;ng.\r\n\r\nSau đ&acirc;y l&agrave; v&agrave;i c&aacute;ch gi&uacute;p bạn truyền đạt th&ocirc;ng điệp một c&aacute;ch hiệu quả nhất.\r\n\r\nTrước ti&ecirc;n, ch&uacute;ng ta sẽ c&ugrave;ng thảo luận về những &yacute; tưởng ch&iacute;nh gi&uacute;p giao tiếp th&agrave;nh c&ocirc;ng, tiếp đ&oacute; l&agrave; l&agrave;m một b&agrave;i trắc nghiệm nho nhỏ để gi&uacute;p bạn nhận ra hiệu quả giao tiếp của bạn tới đ&acirc;u. Nhờ đ&oacute;, bạn nhận ra khu vực m&agrave; bạn n&ecirc;n tập trung v&agrave;o để cải thiện. Những b&agrave;i viết dưới đ&acirc;y sẽ gi&uacute;p bạn ph&aacute;t triển những kỹ năng cần thiết gi&uacute;p giao tiếp hiệu quả v&agrave; truyền cảm hứng trong c&aacute;ch n&oacute;i chuyện, c&aacute;ch viết v&agrave; c&aacute;ch giao tiếp tr&ecirc;n c&aacute;c phương tiện điện tử cho c&aacute;c c&aacute; nh&acirc;n v&agrave; tổ chức.\r\n\r\nĐể đọc mỗi b&agrave;i viết theo thứ tự, bấm &ldquo;Tiếp theo&rdquo; ở cuối trang, hoặc nếu bạn muốn t&igrave;m hiểu th&ecirc;m về những kỹ năng giao tiếp kh&aacute;c, h&atilde;y xem &lsquo;Những bước tiếp theo&rsquo; hoặc những li&ecirc;n kết mở rộng b&ecirc;n dưới.',NULL,NULL,'blog','blog:post','blog:posts','10','blog/2014/08/ky-nang-giao-tiep','admin/blog/edit/10','admin/blog/delete/10'),(141,'Phân tích SWOT','Đ&acirc;y l&agrave; b&agrave;i viết gi&uacute;p bạn t&igrave;m ra c&ocirc;ng cụ v&agrave; phương ph&aacute;p quản l&yacute; nghề nghiệp một c&aacute;ch th&agrave;nh c&ocirc;ng.\r\n\r\nKhởi động l&agrave; 5 b&agrave;i viết gi&uacute;p bạn nghĩ về hướng chọn nghề để kh&aacute;m ph&aacute; ra điều bạn l&agrave;m tốt nhất, loại c&ocirc;ng việc bạn th&iacute;ch l&agrave;m nhất. Từ đ&oacute;, bạn c&oacute; thể t&acirc;n dụng tốt nhất t&agrave;i năng v&agrave; sở th&iacute;ch của m&igrave;nh d&ugrave; ở vai tr&ograve; hiện tại hay một vai tr&ograve; mới.\r\n\r\nTrong đ&oacute;, b&agrave;i&nbsp;Ph&acirc;n t&iacute;ch SWOT&nbsp;bản th&acirc;n sẽ chỉ cho bạn biết c&aacute;ch sử dụng c&ocirc;ng cụ SWOT để t&igrave;m ra điểm mạnh v&agrave; điểm yếu, chỉ r&otilde; cơ hội v&agrave; th&aacute;ch thức để từ đ&oacute; phục vụ tốt nhất cho cuộc sống v&agrave; nghề nghiệp bản th&acirc;n. Từ b&agrave;i n&agrave;y, c&oacute; thể bạn sẽ dễ d&agrave;ng li&ecirc;n tưởng v&agrave; ph&aacute;c thảo ra loại c&ocirc;ng việc m&agrave; bạn c&oacute; thể l&agrave;m tốt nhất. C&oacute; thể đ&acirc;u đ&oacute; trong tiềm thức bạn đ&atilde; l&agrave;m c&ocirc;ng việc n&agrave;y rồi, nhưng qua b&agrave;i viết n&agrave;y bạn sẽ c&oacute; một phương ph&aacute;p tiếp cận nghi&ecirc;m ngặt v&agrave; ch&iacute;nh x&aacute;c hơn để t&igrave;m ra c&ocirc;ng việc l&yacute; tưởng cho m&igrave;nh.\r\n\r\nNh&oacute;m b&agrave;i tiếp theo b&agrave;n về c&aacute;ch ph&aacute;t triển nghề nghiệp sẽ chỉ r&otilde; những g&igrave; bạn c&oacute; thể l&agrave;m để định h&igrave;nh nghề nghiệp v&agrave; chuẩn bị cho việc thăng tiến. Nếu bạn bị xao nh&atilde;ng do l&agrave;m việc qu&aacute; sức m&agrave; lại kh&ocirc;ng được c&ocirc;ng nhận, h&atilde;y t&igrave;m ra c&aacute;ch để được nh&igrave;n nhận xứng đ&aacute;ng.\r\n\r\nTất nhi&ecirc;n, d&ugrave; cho bạn c&oacute; nỗ lực v&agrave; chuẩn bị tốt như thế n&agrave;o, con đường nghề nghiệp c&oacute; thể sẽ kh&ocirc;ng bằng phẳng như bạn hoạch định, đặc biệt trong thời điểm nhạy cảm hiện nay. Bởi vậy, những b&agrave;i viết n&agrave;y sẽ chỉ cho bạn c&aacute;ch sống chung với sự bấp b&ecirc;nh trong c&ocirc;ng việc v&agrave; c&aacute;ch th&iacute;ch nghi với cuộc sống sau khi mất việc. Thậm ch&iacute; khi nền kinh tế đang phất l&ecirc;n, bạn vẫn c&oacute; thể đối mặt với nhiều thử th&aacute;ch chẳng hạn như &ldquo;r&agrave;o cản v&ocirc; h&igrave;nh&rdquo;. H&atilde;y t&igrave;m ra c&aacute;i bạn c&oacute; thể l&agrave;m để ph&aacute; vỡ những điều n&agrave;y!\r\n\r\nSẽ c&oacute; th&ecirc;m hai b&agrave;i viết bổ sung cho phần n&agrave;y gi&uacute;p bạn chuyển tiếp nghề nghiệp bằng những sự kiện quan trọng. Để xem bạn sẽ l&agrave;m g&igrave; nếu c&oacute; một &ocirc;ng chủ mới, v&agrave; l&agrave;m sao để sắp xếp c&ocirc;ng việc ổn thỏa trước khi nghỉ việc.\r\n\r\nH&atilde;y bắt đầu t&igrave;m hiểu bằng c&aacute;ch nhấn v&agrave;o b&agrave;i viết tiếp theo dưới đ&acirc;y.',NULL,NULL,'blog','blog:post','blog:posts','11','blog/2014/08/phan-tich-swot','admin/blog/edit/11','admin/blog/delete/11'),(260,'Lâm Thị Phương Thảo','',NULL,NULL,'pages','pages:page','pages:pages','49','nhan-su/lam-thi-phuong-thao','admin/pages/edit/49','admin/pages/delete/49'),(223,'Những điều sinh viên năm nhất không hề biết','Bạn sẽ kh&ocirc;ng thể ngủ đến trưa\r\n\r\nVới phương thức học t&iacute;n chỉ, bạn đ&atilde; từng nghĩ m&igrave;nh sẽ tự lập một thời gian biểu cho ri&ecirc;ng m&igrave;nh v&agrave; l&agrave;m những điều m&igrave;nh th&iacute;ch. Nhưng sự thật th&igrave; kh&ocirc;ng phải thế, qu&aacute; nhiều sinh vi&ecirc;n đăng k&yacute; t&iacute;n chỉ khiến cho những lớp học v&agrave;o thời điểm &ldquo;v&agrave;ng&rdquo; thường ngay lập tức bị đầy. Nếu kh&ocirc;ng muốn bị thiếu t&iacute;n chỉ, bạn sẽ phải đăng k&yacute; học v&agrave;o những lớp buổi s&aacute;ng sớm hoặc chiều muộn. Điều n&agrave;y c&oacute; nghĩa l&agrave; bạn kh&ocirc;ng thể ngủ v&agrave; nghỉ ngơi đến tận trưa như bạn muốn.\r\n\r\nBạn c&oacute; thể kh&ocirc;ng hợp với bạn c&ugrave;ng ph&ograve;ng của bạn\r\n\r\nBạn c&oacute; thể cảm thấy &aacute;p lực khi phải chung sống với bạn c&ugrave;ng ph&ograve;ng. C&aacute;c bạn c&oacute; thể sống chung nhưng t&iacute;nh c&aacute;ch lại kh&ocirc;ng hợp nhau, hoặc đ&ocirc;i l&uacute;c th&oacute;i quen v&agrave; sự v&ocirc; t&acirc;m của người kia l&agrave;m bạn kh&oacute; chịu? Bạn cũng kh&ocirc;ng thể tr&aacute;nh được những cuộc c&atilde;i v&atilde; với những người bạn c&ugrave;ng ph&ograve;ng khi phải chia sẻ một kh&ocirc;ng gian chật hẹp. Tuy nhi&ecirc;n, đ&acirc;y mới l&agrave; thời điểm để c&aacute;c bạn học c&aacute;ch biết chấp nhận, nhường nhịn v&agrave; chung sống h&ograve;a b&igrave;nh với tất cả mọi người.\r\n\r\nC&uacute;p tiết học kh&ocirc;ng phải l&agrave; &yacute; kiến hay\r\n\r\nC&oacute; thể những lời giảng của giảng vi&ecirc;n l&agrave;m bạn cảm thấy kh&oacute; hiểu, buồn ngủ v&agrave; kh&ocirc;ng muốn c&oacute; mặt trong c&aacute;c buổi học. Bạn muốn nghỉ học trong thời lượng cho ph&eacute;p để c&oacute; tự do ri&ecirc;ng. Nhưng thực tế, những b&agrave;i giảng của thầy gi&aacute;o kh&ocirc;ng bao giờ l&agrave; v&ocirc; nghĩa trong cuộc sống v&agrave; gần nhất l&agrave; trong kỳ thi. Qua b&agrave;i thi giữa kỳ v&agrave; cuối kỳ, c&aacute;c giảng vi&ecirc;n sẽ nhận ra ngay mức độ ch&uacute; &yacute; nghe giảng v&agrave; năng lực của sinh vi&ecirc;n.\r\n\r\nTrang phục l&agrave; một ch&uacute;t kh&aacute;c nhau với thời trung học\r\n\r\nH&atilde;y chắc chắn rằng bạn sẽ mua những trang phục ph&ugrave; hợp với ng&ocirc;i trường m&agrave; bạn theo học. V&igrave; vậy, nếu bạn xuất hiện trong một bộ trang phục qu&aacute; kỳ dị v&agrave; kh&aacute;c với những sinh vi&ecirc;n kh&aacute;c, họ sẽ t&ograve; m&ograve; v&agrave; b&agrave;n t&aacute;n v&agrave; l&agrave;m bạn bối rối.\r\n\r\nNhưng c&aacute;c ch&agrave;ng trai th&igrave; kh&ocirc;ng kh&aacute;c nam sinh trung học\r\n\r\nMọi người đều n&oacute;i với bạn rằng tất cả c&aacute;c anh ch&agrave;ng sinh vi&ecirc;n kh&oacute;a tr&ecirc;n dễ thương đang chờ đ&oacute;n bạn ở trường đại học. V&igrave; vậy, bạn c&oacute; thể đứng cả buổi ngo&agrave;i h&agrave;nh lang để chờ &ldquo;anh ấy&rdquo; đi qua. Nhưng sự thật l&agrave; c&aacute;c ch&agrave;ng sinh vi&ecirc;n trường đại học cũng ngại ngần v&agrave; xấu hổ khi đứng trước c&aacute;c c&ocirc; g&aacute;i y như thời họ c&ograve;n học trung học. V&agrave; việc l&agrave;m quen hay hẹn h&ograve; với một ch&agrave;ng sinh vi&ecirc;n kh&oacute;a tr&ecirc;n c&ograve;n phụ thuộc rất nhiều v&agrave;o duy&ecirc;n số v&agrave; t&iacute;nh c&aacute;ch của hai bạn.&nbsp;\r\n\r\nGiảng vi&ecirc;n kh&ocirc;ng muốn cho bạn trượt đ&acirc;u\r\n\r\nC&oacute; lẽ bạn đ&atilde; từng nghe n&oacute;i rằng, c&aacute;c giảng vi&ecirc;n trong trường đại học rất bận v&agrave; kh&ocirc;ng c&oacute; thời gian để quan t&acirc;m đến từng sinh vi&ecirc;n trong trường đại học. Tuy nhi&ecirc;n, họ vẫn rất mong bạn sẽ tiến bộ v&agrave; tr&ocirc;ng đợi rất nhiều ở bạn. Họ kh&ocirc;ng bao giờ mong bạn sẽ trượt v&agrave; thất bại. &nbsp;Nếu bạn đang gặp kh&oacute; khăn trong một b&agrave;i giảng, họ mong bạn sẽ thắc mắc. Trong h&agrave;ng trăm, đ&ocirc;i khi h&agrave;ng ng&agrave;n sinh vi&ecirc;n, họ c&oacute; thể kh&ocirc;ng nhớ nổi t&ecirc;n sinh vi&ecirc;n, thế n&ecirc;n họ sẽ kh&ocirc;ng thể theo d&otilde;i bạn trong c&aacute;c tiết học, v&igrave; vậy, bạn phải l&agrave; người chủ động tiếp cận với giảng vi&ecirc;n để c&oacute; được những kiến thức qu&yacute; b&aacute;u từ họ.&nbsp;\r\n\r\nBạn sẽ c&oacute; cả tấn bạn b&egrave;&hellip; nhưng rất &iacute;t t&igrave;nh bạn k&eacute;o d&agrave;i qu&aacute; 4 năm\r\n\r\nỞ trường đại học, bạn sẽ kết bạn với rất nhiều người, trong lớp học, trong c&aacute;c c&acirc;u lạc bộ m&agrave; bạn tham gia, nơi bạn l&agrave;m th&ecirc;m&hellip; Nhưng bạn sẽ mau ch&oacute;ng thất vọng bởi những người bạn đ&oacute; sẽ nhanh ch&oacute;ng biến mất sau một khoảng thời gian c&aacute;c bạn kh&ocirc;ng gặp v&agrave; tiếp x&uacute;c thường xuy&ecirc;n. &nbsp;Sự thật l&agrave; cho đến khi ra trường, bạn chỉ c&ograve;n li&ecirc;n lạc v&agrave; gắn b&oacute; với rất &iacute;t bạn, thậm ch&iacute; l&agrave; kh&ocirc;ng người bạn n&agrave;o cả.&nbsp;\r\n\r\nPh&ograve;ng trọ hay ph&ograve;ng ở k&yacute; t&uacute;c x&aacute; kh&ocirc;ng sang trọng như bạn nghĩ\r\n\r\nỞ những th&agrave;nh phố lớn, ph&ograve;ng trọ hoặc ph&ograve;ng ở k&yacute; t&uacute;c x&aacute; chỉ cung cấp cho bạn được điều kiện ăn, ở, học tập ở mức tối thiểu. Bạn sẽ cảm thấy nhớ nh&agrave; v&agrave; căn ph&ograve;ng ở nh&agrave; m&igrave;nh v&ocirc; c&ugrave;ng. Nhưng dần dần bạn sẽ quen v&agrave; cảm thấy thoải m&aacute;i với cuộc sống của sinh vi&ecirc;n.',NULL,NULL,'blog','blog:post','blog:posts','26','blog/2014/09/nhung-dieu-sinh-vien-nam-nhat-khong-he-biet','admin/blog/edit/26','admin/blog/delete/26'),(224,'Tân sinh viên đừng bỏ học vì học phí','Trường hợp do nh&agrave; qu&aacute; ngh&egrave;o m&agrave; mẹ phải tự tử để lấy tiền ph&uacute;ng viếng cho con đi học như trường hợp của chị Nguyễn Thị Mỹ Nh&acirc;n (48 tuổi) ở x&atilde; An Xuy&ecirc;n, TP C&agrave; Mau đ&atilde; g&acirc;y r&uacute;ng động x&atilde; hội.\r\nCon trai lớn của chị l&agrave; Đinh C&ocirc;ng Bằng thi đậu CĐ ở B&agrave; Rịa - Vũng T&agrave;u năm 2013 nhưng do nh&agrave; ngh&egrave;o, chị lại bị bệnh tật n&ecirc;n kh&ocirc;ng c&oacute; khả năng lo học ph&iacute; cho con. Đường c&ugrave;ng chị nghĩ đến c&aacute;i chết với mong muốn lấy số tiền ph&uacute;ng viếng của m&igrave;nh để cho con đi học đại học.\r\n\r\nKỳ thi tuyển sinh năm nay cũng c&oacute; nhiều sỹ tử đậu đại học nhưng nh&agrave; ngh&egrave;o n&ecirc;n đ&agrave;nh g&aacute;c lại ước muốn học đại học.\r\n\r\nTrường hợp của em Trần Thị Tr&uacute;c Mai, cựu học sinh lớp 12A6 Trường THPT Đốc Binh Kiều (thị x&atilde; Cai Lậy, tỉnh Tiền Giang) l&agrave; một v&iacute; dụ. Mai vừa đậu v&agrave;o ng&agrave;nh Th&uacute; y Trường Đại học N&ocirc;ng l&acirc;m TP.HCM.\r\n\r\nGia đ&igrave;nh Mai thuộc diện hộ cận ngh&egrave;o, nh&agrave; kh&ocirc;ng c&oacute; đất đai n&ecirc;n cha mẹ Mai đi l&agrave;m thu&ecirc; kiếm sống. Khi nghe tin con đậu đại học, cha mẹ Mai rất vui mừng nhưng nh&agrave; ngh&egrave;o, để c&oacute; một khoản tiền lớn để đ&oacute;ng học ph&iacute;, tiền s&aacute;ch vở, tiền thu&ecirc; nh&agrave; trọ, tiền ăn&hellip; cho Mai, cả gia đ&igrave;nh thực sự đau đầu. Mai c&oacute; nguy cơ dở dang việc học v&igrave; ho&agrave;n cảnh gia đ&igrave;nh kh&oacute; khăn.\r\n\r\nHo&agrave;n cảnh của t&acirc;n sinh vi&ecirc;n Nguyễn Minh Dương cũng vậy. Em đậu trường đại học C&ocirc;ng nghiệp TP.HCM ng&agrave;nh c&ocirc;ng nghệ kỹ thuật h&oacute;a học, nhưng nay đ&atilde; qu&aacute; thời hạn nhập học hơn nửa th&aacute;ng m&agrave; em vẫn chưa c&oacute; đủ tiền đ&oacute;ng học ph&iacute; v&agrave; đang đứng trước nguy cơ phải từ bỏ giảng đường đại học.\r\n\r\nGia cảnh của Dương rất ngh&egrave;o kh&oacute;, ba đi l&agrave;m thu&ecirc;, mẹ bị động kinh. Dương vẫn đang phụ gi&uacute;p gia đ&igrave;nh, đi l&agrave;m thu&ecirc; như ba. Ch&iacute;nh v&igrave; vậy, số tiền học ph&iacute; gần 4 triệu đồng l&agrave; nỗi lo của gia đ&igrave;nh Dương.\r\n\r\nNhững trường hợp sinh vi&ecirc;n ngh&egrave;o học giỏi như thế n&agrave;y c&oacute; thể tự vươn l&ecirc;n bằng c&aacute;ch vay vốn tại ng&acirc;n h&agrave;ng Ch&iacute;nh s&aacute;ch X&atilde; hội. C&aacute;c em c&oacute; thể c&ugrave;ng gia đ&igrave;nh l&ecirc;n UBND x&atilde;, phường của m&igrave;nh xin giấy x&aacute;c nhận l&agrave; hộ ngh&egrave;o, cận ngh&egrave;o, đối tượng c&oacute; ho&agrave;n cảnh kh&oacute; khăn, mồ c&ocirc;i sẽ được vay vốn trang trải cho chi ph&iacute; học tập của m&igrave;nh.\r\n\r\n&Ocirc;ng Nguyễn Văn Ti&ecirc;n, Ph&oacute; gi&aacute;m đốc ng&acirc;n h&agrave;ng Ch&iacute;nh s&aacute;ch chi nh&aacute;nh TP.HCM cho biết, đ&acirc;y l&agrave; ng&acirc;n h&agrave;ng cho vay theo chỉ định của Ch&iacute;nh phủ n&ecirc;n người vay chỉ cần đ&aacute;p ứng đủ điều kiện l&agrave; được vay m&agrave; kh&ocirc;ng phải c&oacute; t&agrave;i sản đảm bảo.\r\n\r\nĐối với khoản cho sinh vi&ecirc;n vay, với mức 1,1 triệu đồng/th&aacute;ng, l&atilde;i suất 0,65%/th&aacute;ng. Sinh vi&ecirc;n sau khi ra trường 01 năm mới phải trả gốc v&agrave; l&atilde;i. Tuy nhi&ecirc;n, l&atilde;i vẫn được t&iacute;nh trong qu&aacute; tr&igrave;nh vay.\r\n\r\nChẳng hạn, mức cho vay đối với 01 sinh vi&ecirc;n trong 01 năm học khoảng 12 triệu/năm, với 05 năm học th&igrave; tổng số tiền c&oacute; thể vay l&agrave; 60 triệu đồng. Ng&acirc;n h&agrave;ng sẽ giải ng&acirc;n cho sinh vi&ecirc;n v&agrave;o đầu mỗi năm học.\r\n\r\nV&agrave;o đầu mỗi năm học, ng&acirc;n h&agrave;ng tại chi nh&aacute;nh TP.HCM nhận khoảng 30.000-40.000 trường hợp xin vay vốn v&agrave; giải ng&acirc;n khoảng 40-50 tỷ đồng. Đ&acirc;y l&agrave; chương tr&igrave;nh cho vay ưu đ&atilde;i lớn thứ 2 trong c&aacute;c chương tr&igrave;nh cho vay ưu đ&atilde;i của ng&acirc;n h&agrave;ng Ch&iacute;nh s&aacute;ch x&atilde; hội với tổng dư nợ đến nay đạt 30.000 tỷ đồng.\r\n\r\nSinh vi&ecirc;n Mai Trần Ngọc Đức, trường đại học Thể dục Thể thao đ&atilde; vay được tại ng&acirc;n h&agrave;ng Ch&iacute;nh s&aacute;ch với số tiền 11 triệu đồng cho năm học 2014. Đ&acirc;y l&agrave; năm thứ 2 Đức được giải ng&acirc;n để trang trải học ph&iacute; cho m&igrave;nh.\r\n\r\nHay sinh vi&ecirc;n Lương Vũ Trọng Đạt, trường đại học Nguyễn Tất Th&agrave;nh cũng được vay 11 triệu đồng để đ&oacute;ng học ph&iacute; cho năm học 2014. Nhờ c&oacute; khoản tiền vay n&agrave;y m&agrave; em y&ecirc;n t&acirc;m học tập.\r\n\r\nSinh vi&ecirc;n Nguyễn Th&aacute;i Kim Hằng cũng được vay 5,5 triệu đồng, đ&acirc;y l&agrave; nguồn tiền rất qu&yacute; b&aacute;u với em b&ecirc;n cạnh tiền em kiếm được từ đi l&agrave;m th&ecirc;m để lo cuộc sống sinh vi&ecirc;n của m&igrave;nh.\r\n\r\n&Ocirc;ng Ti&ecirc;n cũng chia sẻ: &ldquo;T&ocirc;i cũng nghe trường hợp thương t&acirc;m ở C&agrave; Mau khi mẹ chết để lấy tiền ph&uacute;ng viếng cho con đi học. Ở đ&acirc;y l&agrave; do gia đ&igrave;nh kh&ocirc;ng được x&aacute;c nhận hộ ngh&egrave;o, nhưng gia đ&igrave;nh c&oacute; thể xin x&aacute;c nhận thuộc diện kh&oacute; khăn, bệnh tật của UBND x&atilde; l&agrave; vẫn c&oacute; thể vay vốn tại ng&acirc;n h&agrave;ng Ch&iacute;nh s&aacute;ch x&atilde; hội&rdquo;.\r\n\r\n&ldquo;Ch&uacute;ng t&ocirc;i hằng năm cũng đều l&agrave;m việc với UBND c&aacute;c x&atilde;, phường để tuy&ecirc;n truyền về chương tr&igrave;nh cho vay sinh vi&ecirc;n ngh&egrave;o, nhưng chắc tại truyền th&ocirc;ng chưa được tốt n&ecirc;n vẫn nghe b&aacute;o ch&iacute; phản &aacute;nh những trường hợp đau l&ograve;ng khi c&oacute; nhiều gia đ&igrave;nh c&oacute; con đậu 2 trường đại học nhưng lại kh&ocirc;ng c&oacute; tiền đ&oacute;ng học ph&iacute;&rdquo;, &ocirc;ng Ti&ecirc;n cho biết th&ecirc;m.\r\n\r\nHiện tỷ lệ nợ xấu m&agrave; sinh vi&ecirc;n ra trường kh&ocirc;ng trả được chiếm rất thấp, khoảng 1%, nhưng đ&acirc;y l&agrave; những trường hợp qu&aacute; kh&oacute; khăn hoặc rủi ro giữa chừng với những nguy&ecirc;n nh&acirc;n kh&aacute;ch quan.\r\n\r\nDo vậy, bằng sự hiểu biết của m&igrave;nh trong x&atilde; hội th&ocirc;ng tin hiện nay, c&aacute;c học tr&ograve; ngh&egrave;o học giỏi vẫn c&oacute; thể tự vươn l&ecirc;n trong học tập v&agrave; trong cuộc sống bằng những nguồn vốn vay ưu đ&atilde;i.',NULL,NULL,'blog','blog:post','blog:posts','27','blog/2014/09/tan-sinh-vien-dung-bo-hoc-vi-hoc-phi','admin/blog/edit/27','admin/blog/delete/27'),(275,'CSS Image Sprites','CSS Sprites rất hay được sử dụng, v&agrave; c&agrave;ng phổ biến hơn trong HTML5 để diễn hoạt c&aacute;c h&agrave;nh động dựa tr&ecirc;n c&aacute;c frame h&igrave;nh trong Sprites. Sprites c&oacute; nghĩa l&agrave; c&oacute; nhiều khung h&igrave;nh trong 1 ảnh.\r\n\r\nĐể hiểu CSS Image Sprites l&agrave; g&igrave;? ch&uacute;ng ta c&ugrave;ng xem x&eacute;t c&aacute;ch thiết kế Navigation như h&igrave;nh b&ecirc;n dưới, nếu theo c&aacute;ch th&ocirc;ng thường bạn phải cần &iacute;t nhất &iacute;t nhất 8 h&igrave;nh ảnh để l&agrave;m ảnh nền (Nếu t&iacute;nh cả 4 ảnh cho hiệu ứng Rollover).\r\n\r\nCSS Image Sprites l&agrave; c&aacute;ch gộp nhiều ảnh nền cần sử dụng v&agrave;o một file h&igrave;nh duy nhất v&agrave; sử dụng thuộc t&iacute;nh background()-position trong CSS với 2 vị tr&iacute; Left, Top để &nbsp;x&aacute;c định vị tr&iacute; th&agrave;nh phần ảnh cần l&agrave;m nền, nhằm giảm tải Request (y&ecirc;u cầu) đến Server, v&agrave; giảm dung lượng file h&igrave;nh, tăng tốc độ tải trang, tiết kiệm t&agrave;i nguy&ecirc;n hệ thống.&nbsp;\r\n\r\nC&aacute;ch sử dụng CSS Image Sprites&nbsp;\r\nTrước ti&ecirc;n bạn phải sử dụng một chương tr&igrave;nh đồ họa như Photoshop&hellip;, v&agrave; gộp c&aacute;c file ảnh cần sử dụng lại th&agrave;nh một file duy nhất. M&igrave;nh gộp 8 file h&igrave;nh cần sử dụng tại v&iacute; dụ tr&ecirc;n th&agrave;nh một file duy nhất với t&ecirc;n icon.gif như sau:&nbsp;\r\n\r\n\r\n&nbsp;&nbsp;\r\nBạn n&ecirc;n sắp xếp cho thuận lợi trong việc t&iacute;nh to&aacute;n vị tr&iacute; của c&aacute;c ảnh.&nbsp;\r\n\r\nT&ocirc;i sắp xếp c&aacute;c &nbsp;ảnh liền kề nhau từ tr&aacute;i sang phải, phần đối xứng b&ecirc;n dưới l&agrave; c&aacute;c h&igrave;nh ảnh sử dụng cho hiệu ứng Rollover.&nbsp;\r\nTiếp theo l&agrave; m&atilde; HTML&nbsp;\r\n&lt;div id=&quot;wrapper&quot;&gt;&nbsp;\r\n&lt;ul id=&quot;icon&quot;&gt;&nbsp;\r\n&nbsp; &nbsp; &nbsp;&lt;li class=&quot;user&quot;&gt;&lt;a href=&quot;#&quot;&gt;User&lt;/a&gt;&lt;/li&gt;&nbsp;\r\n&nbsp; &nbsp; &nbsp;&lt;li class=&quot;rss&quot;&gt;&lt;a href=&quot;#&quot;&gt;Rss&lt;/a&gt;&lt;/li&gt;&nbsp;\r\n&nbsp; &nbsp; &nbsp;&lt;li class=&quot;comment&quot;&gt;&lt;a href=&quot;#&quot;&gt;Comment&lt;/a&gt;&lt;/li&gt;&nbsp;\r\n&nbsp; &nbsp; &nbsp;&lt;li class=&quot;global&quot;&gt;&lt;a href=&quot;#&quot;&gt;Global&lt;/a&gt;&lt;/li&gt;&nbsp;\r\n&lt;/ul&gt;&nbsp;\r\n&lt;/div&gt;\r\n\r\nM&atilde; CSS&nbsp;\r\n{margin: 0; padding: 0}&nbsp;\r\n#wrapper{margin: 30px auto; width: 500px}&nbsp;\r\n#icon li{float: left; margin-right: 50px; list-style: none}&nbsp;\r\n#icon li a{height: 85px; display: block; background():url(imgs/icon.gif); text-indent: -999999px}&nbsp;\r\n\r\nỞ đoạn m&atilde; CSS tr&ecirc;n ch&uacute;ng ta sử dụng file h&igrave;nh icon.gif &nbsp;duy nhất l&agrave;m nền cho c&aacute;c th&agrave;nh phần li&ecirc;n kết (#icon li a)\r\n\r\nTiếp theo &nbsp;ch&uacute;ng ta sẽ Sử dụng thuộc t&iacute;nh background()-position để x&aacute;c định vị tr&iacute; th&agrave;nh phần ảnh cho c&aacute;c th&agrave;nh phần trong Navigation.&nbsp;\r\n\r\nTừ &nbsp;Photoshop bạn c&oacute; thể x&aacute;c định được k&iacute;ch thước c&aacute;c th&agrave;nh phần ảnh như sau:&nbsp;\r\n\r\n\r\n\r\n+ &nbsp;Th&agrave;nh phần icon user (#icon li.user a) sử dụng phần tr&ecirc;n c&ugrave;ng b&ecirc;n tr&aacute;i của file ảnh icon.gif, n&ecirc;n c&oacute; vị tr&iacute; : left: 0px, top: 0px v&agrave; c&oacute; độ rộng 54px.&nbsp;\r\n\r\n#icon li.user a{width: 54px; background()-position: 0 0}\r\n\r\n+ Th&agrave;nh phần icon rss(#icon li.rss a)&nbsp;\r\nTh&agrave;nh phẩn ảnh chứa icon RSS c&oacute; vị tr&iacute; c&aacute;ch b&ecirc;n tr&aacute;i 54px (Bằng độ lớn của th&agrave;nh phần (#icon li.user a), do đ&oacute; ch&uacute;ng ta phải di chuyển th&agrave;nh phần ảnh n&agrave;y sang tr&aacute;i -54px để trở về đ&uacute;ng vị tr&iacute; 0 0, v&agrave; c&oacute; độ rộng 51px.&nbsp;\r\n\r\n#icon li.rss a{width: 51px; background()-position: -54px 0}\r\n\r\nTương tự th&agrave;nh phần icon Comment ch&uacute;ng ta phải dịch chuyển về b&ecirc;n tr&aacute;i 54px (độ lớn icon User) + 51px (Độ lớn icon RSS) = 105px&nbsp;\r\n\r\n#icon li.comment a{width: 61px; background()-position: -105px 0}\r\n\r\nV&agrave; icon Global dịch chuyển 105 + 61 = 166px&nbsp;\r\n\r\n#icon li.global a{width: 54px; background()-position: -166px 0}\r\n\r\nC&aacute;c th&agrave;nh phần ảnh tạo hiệu ứng Rollover c&oacute; vị trị left giống như tr&ecirc;n, chỉ thay đổi vị tr&iacute; top được dịch chuyển xuống ph&iacute;a dưới 85px (Bằng chiều cao của c&aacute;c Icon), n&ecirc;n ta c&oacute; m&atilde; CSS như sau:&nbsp;\r\n\r\n#icon li.user a:hover{background()-position: 0 85px}&nbsp;\r\n#icon li.rss a:hover{background()-position: -54px 85px}&nbsp;\r\n#icon li.comment a:hover{background()-position: -105px 85px}&nbsp;\r\n#icon li.global a:hover{background()-position: -166px 85px}\r\n\r\nKết Luận\r\nVị trị left của th&agrave;nh phần ảnh ch&iacute;nh l&agrave; gi&aacute; trị &acirc;m độ lớn khoảng c&aacute;ch của n&oacute; so với b&ecirc;n tr&aacute;i. V&iacute; dụ Icon Comment c&aacute;ch b&ecirc;n tr&aacute;i 105px n&ecirc;n vị tr&iacute; left của n&oacute; l&agrave;: -105px. Vị tr&iacute; top ch&iacute;nh l&agrave; khoảng c&aacute;ch độ lớn khoảng c&aacute;ch giữa n&oacute; so với b&ecirc;n tr&ecirc;n.&nbsp;',NULL,NULL,'blog','blog:post','blog:posts','33','blog/2014/09/css-image-sprites','admin/blog/edit/33','admin/blog/delete/33'),(273,'Tăng tốc độ tải trang web với jQuery Lazyload Plugin','Lazyload l&agrave; plugin của jQuery cải thiện việc tải h&igrave;nh ảnh ở những trang web d&agrave;i. Ảnh b&ecirc;n ngo&agrave;i khung h&igrave;nh sẽ chỉ thể hiện khi người d&ugrave;ng k&eacute;o chuột tới. Sử dụng Lazyload ở những trang web d&agrave;i với nhiều h&igrave;nh ảnh sẽ gi&uacute;p cho trang được tải nhanh hơn, trong một số trường hợp n&oacute; c&ograve;n gi&uacute;p giảm tải cho m&aacute;y chủ web.\r\n\r\nDescription:\r\nNhư c&aacute;i t&ecirc;n gọi của n&oacute;: &ldquo;Lazy&rdquo;. H&igrave;nh ảnh tr&ecirc;n web của bạn sẽ được delay để tải sau, do đ&oacute; thay v&igrave; tải h&igrave;nh, tr&igrave;nh duyệt sẽ lo phần nội dung text trước. Tốc độ được cải thiện đ&aacute;ng kể.\r\nH&igrave;nh ảnh chỉ được tải khi n&oacute; lọt v&agrave;o tầm nh&igrave;n của người d&ugrave;ng\r\n\r\nC&aacute;ch sử dụng&nbsp;\r\nTrước ti&ecirc;n, bạn cần download jQuery v&agrave; Lazyload plugin. Sau đ&oacute; th&ecirc;m v&agrave;o trong thẻ &lt;head&gt; của trang web. Bạn c&oacute; thể dowload lazy load plugin tại:\r\nhttps://raw.githubusercontent.com/tuupola/jquery_lazyload/master/jquery.lazyload.js\r\nSau đ&oacute; để sử dụng lazy load bạn chỉ cần một c&acirc;u lệnh đơn giản:\r\n$(&lsquo;img&rsquo;).lazyload();\r\n\r\nT&ugrave;y biến theo y&ecirc;u cầu c&aacute; nh&acirc;n&nbsp;\r\nLazyload cung cấp một số lựa chọn như load 1 phần của ảnh, d&ugrave;ng ảnh thay thế, gọi sự kiện, hiệu ứng khi xuất hiện ảnh, đặt thời gian tải ảnh... Bạn c&oacute; thể xem th&ecirc;m tại trang chủ của lazyload: http://www.appelsiini.net/projects/lazyload\r\nĐoạn m&atilde; dưới đ&acirc;y sẽ tải trước 200px của ảnh trước khi ảnh xuất hiện&nbsp;\r\n\r\n$(&quot;img&quot;).lazyload({ threshold : 200 });\r\n\r\nD&ugrave;ng ảnh thay thế&nbsp;\r\n$(&quot;img&quot;).lazyload({ placeholder : &quot;img/grey.gif&quot; });\r\n\r\nẢnh thay thế đi c&ugrave;ng plugin ảnh gif 1x1px, bạn c&oacute; thể thay bằng đường dẫn ảnh ri&ecirc;ng của m&igrave;nh&nbsp;\r\n\r\nSử dụng hiệu ứng mờ khi xuất hiện&nbsp;\r\n$(&quot;img&quot;).lazyload({ placeholder : &quot;img/grey.gif&quot;, effect : &quot;fadeIn&quot;});\r\n\r\nDemo tại đ&acirc;y: http://www.appelsiini.net/projects/lazyload/enabled.html',NULL,NULL,'blog','blog:post','blog:posts','31','blog/2014/09/tang-toc-do-tai-trang-web-voi-jquery-lazyload-plugin','admin/blog/edit/31','admin/blog/delete/31'),(225,'Lời khuyên để có nhiều bạn bè hơn','C&oacute; được những người bạn tốt l&agrave; một điều may mắn. V&agrave; c&oacute; rất nhiều c&aacute;ch kh&aacute;c nhau để bạn l&agrave;m quen v&agrave; c&oacute; những người bạn mới ở trường.&nbsp;\r\n\r\n1. Sử dụng mạng x&atilde; hội\r\n\r\nNg&agrave;y nay với sự b&ugrave;ng nổ của mạng x&atilde; hội, bạn c&oacute; thể gặp gỡ v&agrave; tr&ograve; chuyện với rất nhiều bạn c&ugrave;ng trường qua Facebook hay Twitter. Rất nhiều người đang tr&ograve; chuyện với những người bạn th&acirc;n của m&igrave;nh qua Facebook v&agrave; c&oacute; nhiều bạn mới qua Facebook. Facebook ng&agrave;y c&agrave;ng trở th&agrave;nh nơi để gặp gỡ v&agrave; kết nối bạn b&egrave;, nhất l&agrave; những người bạn c&ugrave;ng trường c&oacute; chung sở th&iacute;ch v&agrave; đam m&ecirc;. Mạng x&atilde; hội l&agrave; nơi dễ d&agrave;ng nhất, tiện dụng nhất để kết bạn trong trường.&nbsp;\r\n\r\n2. Tham gia c&acirc;u lạc bộ\r\n\r\nCho d&ugrave; bạn l&agrave; một sinh vi&ecirc;n năm đầu ti&ecirc;n hoặc l&agrave; sinh vi&ecirc;n năm thứ tư, kh&ocirc;ng bao giờ l&agrave; qu&aacute; muộn để tham gia một c&acirc;u lạc bộ. H&atilde;y tham gia c&acirc;u lạc bộ những người c&oacute; chung sở th&iacute;ch, đam m&ecirc; để bạn c&oacute; thể gặp gỡ v&agrave; kết nối với những người c&ugrave;ng niềm đam m&ecirc; với bạn. Đ&oacute; c&oacute; thể l&agrave; c&acirc;u lạc bộ ghi ta, v&otilde; thuật, tiếng anh, hoặc thậm ch&iacute; l&agrave; một c&acirc;u lạc bộ những người h&acirc;m mộ của một ng&ocirc;i sao n&agrave;o đ&oacute;. Tham gia c&acirc;u lạc bộ l&agrave; cơ hội để bạn c&oacute; thể tr&ograve; chuyện, kết bạn với rất nhiều người.\r\n\r\n3. Bắt đầu một c&ocirc;ng việc partime\r\n\r\nNếu c&oacute; thời gian, h&atilde;y bắt đầu một c&ocirc;ng việc l&agrave;m th&ecirc;m. C&aacute;c bạn sẽ được l&agrave;m việc c&ugrave;ng một m&ocirc;i trường, c&ugrave;ng một sếp v&agrave; với những kh&aacute;ch h&agrave;ng giống nhau. Thật kh&ocirc;ng kh&oacute; để kết bạn v&agrave; chia sẻ c&ugrave;ng với những nh&acirc;n vi&ecirc;n kh&aacute;c. C&aacute;c bạn thậm ch&iacute; sẽ c&oacute; thể t&igrave;m thấy những người bạn tốt c&ugrave;ng l&agrave;m ở những chỗ l&agrave;m th&ecirc;m. &nbsp;\r\n\r\n4. Tham gia hội đồng hương\r\n\r\nỞ nhiều trường, thậm ch&iacute; tr&ecirc;n to&agrave;n th&agrave;nh phố, rất nhiều bạn sinh vi&ecirc;n đ&atilde; tự tổ chức n&ecirc;n một hội đồng hương để kết nối những bạn sinh vi&ecirc;n c&oacute; c&ugrave;ng qu&ecirc; hương. Đ&acirc;y l&agrave; cơ hội tuyệt vời để c&aacute;c bạn c&ugrave;ng &ocirc;n lại kỷ niệm khi c&ograve;n ở qu&ecirc; hương, khơi dậy l&ograve;ng tự h&agrave;o về qu&ecirc; hương trong m&igrave;nh v&agrave; l&agrave;m quen với những người bạn mới.\r\n\r\n5. Kh&ocirc;ng gượng &eacute;p\r\n\r\nĐừng cố &eacute;p m&igrave;nh phải th&acirc;n với bạn n&agrave;y, bạn kia chỉ v&igrave; đ&oacute; l&agrave; bạn c&ugrave;ng ph&ograve;ng, bạn c&ugrave;ng lớp với bạn. H&atilde;y để t&igrave;nh bạn đến tự nhi&ecirc;n với những người muốn l&agrave;m bạn với nhau thật sự. Sự gượng &eacute;p để cố gắng mở rộng c&aacute;c mối quan hệ sẽ g&acirc;y phản t&aacute;c dụng v&agrave; cuối c&ugrave;ng bạn sẽ nhận ra m&igrave;nh vẫn c&ocirc; đơn thực sự.&nbsp;\r\n\r\n6. Tiếp tục l&agrave;m những g&igrave; bạn th&iacute;ch\r\n\r\nĐừng bỏ qua những sở th&iacute;ch, niềm đam m&ecirc; đặc biệt chỉ v&igrave; nghĩ rằng bạn đ&atilde; trở th&agrave;nh sinh vi&ecirc;n. V&igrave; biết đ&acirc;u rằng bạn sẽ gặp rất nhiều bạn b&egrave; c&oacute; chung sở th&iacute;ch v&agrave; niềm đam m&ecirc; trong trường.',NULL,NULL,'blog','blog:post','blog:posts','28','blog/2014/09/loi-khuyen-de-co-nhieu-ban-be-hon','admin/blog/edit/28','admin/blog/delete/28'),(226,'Biến dạng cơ thể vì thường xuyên ngồi sai tư thế','G&ugrave; lưng, vẹo cột sống\r\n\r\nNhiều người trong ch&uacute;ng ta thường kh&ocirc;ng ch&uacute; &yacute; đến c&aacute;ch ngồi khi đang học b&agrave;i, l&agrave;m việc. Điều n&agrave;y kh&ocirc;ng tốt ch&uacute;t n&agrave;o bởi nếu c&aacute;c bạn kh&ocirc;ng kịp điều chỉnh v&agrave; ngồi sai tư thế trong thời gian l&acirc;u d&agrave;i th&igrave; cột sống sẽ bị cong vẹo, xuất hiện đường uốn nghi&ecirc;ng l&agrave;m lưng g&ugrave; r&otilde; rệt. Điều n&agrave;y kh&ocirc;ng chỉ g&acirc;y mất thẩm mỹ m&agrave; c&ograve;n g&acirc;y tổn hại đến sức khỏe sau n&agrave;y. Những người phải ngồi nhiều như sinh vi&ecirc;n, học sinh hay nh&acirc;n vi&ecirc;n văn ph&ograve;ng rất cần lưu &yacute; đến điều n&agrave;y v&igrave; một khi cột sống đ&atilde; bị tổn thương v&agrave; g&acirc;y ra biến dị th&igrave; rất kh&oacute; để kh&ocirc;i phục lại.\r\n\r\nG&acirc;y ảnh hưởng đến v&ograve;ng một\r\n\r\nNhiều bạn kh&ocirc;ng biết rằng ngồi sai tư thế c&ograve;n ảnh hưởng đến h&igrave;nh d&aacute;ng của v&ograve;ng một. C&oacute; rất nhiều bạn thường c&oacute; th&oacute;i quen t&igrave; s&aacute;t ngực v&agrave;o b&agrave;n khi viết. Điều n&agrave;y v&ocirc; t&igrave;nh khiến v&ograve;ng một bị ma s&aacute;t, phải chịu &aacute;p lực đ&egrave; lớn khiến n&uacute;i đ&ocirc;i dễ bị chảy xệ sang hai b&ecirc;n. Ngay cả khi bạn ngồi lệch sang một b&ecirc;n, khiến cho cột sống bị lệch, vai cao vai thấp cũng sẽ khiến n&uacute;i đ&ocirc;i bị biến dạng, một b&ecirc;n cao, một b&ecirc;n thấp v&agrave; chảy xệ theo.\r\n\r\nẢnh hưởng đến xương chậu\r\n\r\nXương chậu được coi l&agrave; nền tảng của cột sống, nếu như những cơ của th&acirc;n được ph&aacute;t triển đều th&igrave; sức k&eacute;o của c&aacute;c cơ co c&acirc;n bằng với sức k&eacute;o của cơ duỗi. L&uacute;c đ&oacute;, đầu v&agrave; th&acirc;n người được giữ thẳng, tư thế b&igrave;nh thường của bả vai, tay, ch&acirc;n phụ thuộc v&agrave;o mức độ ph&aacute;t triển v&agrave; sự căng cơ của c&aacute;c nh&oacute;m cơ.\r\n\r\n​\r\n\r\nLệch vai\r\n\r\nV&igrave; khi ngồi sai tư thế như nghi&ecirc;ng, vẹo người sang một b&ecirc;n khi đọc viết sẽ khiến một b&ecirc;n vai bị xệ, một b&ecirc;n cao hơn, k&egrave;m theo đ&oacute; l&agrave; việc cột sống cũng bị lệch sang một b&ecirc;n. L&uacute;c n&agrave;y, d&aacute;ng người của ch&uacute;ng m&igrave;nh sẽ rất mất thẩm mỹ v&igrave; đ&atilde; bị biến dạng do ngồi sai tư thế qu&aacute; l&acirc;u.\r\n\r\nẢnh hưởng tới sức khỏe một c&aacute;ch &ldquo;to&agrave;n diện&rdquo;\r\n\r\nNhững tư thế ngồi sai như vẹo người sang một b&ecirc;n khi viết, nằm b&ograve; ra b&agrave;n, c&ograve;ng lưng, c&uacute;i s&aacute;t mặt xuống vở&hellip; kh&ocirc;ng những khiến cơ thể bị biến dạng m&agrave; c&ograve;n ảnh hưởng đến c&aacute;c cơ quan kh&aacute;c như tim, phổi. B&ecirc;n cạnh đ&oacute;, điều n&agrave;y c&ograve;n ảnh hưởng đến mắt, g&acirc;y ra c&aacute;c tật kh&uacute;c xạ cận, loạn thị do c&uacute;i gằm mặt, nằm b&ograve; ra b&agrave;n.\r\n\r\nNếu như thấy b&agrave;n ghế ngồi kh&ocirc;ng ph&ugrave; hợp hay d&aacute;ng đi c&oacute; dấu hiệu như vai th&otilde;ng xuống, lưng g&ugrave; g&ugrave;&hellip; th&igrave; cần phải thay đổi, điều chỉnh ngay tư thế ngồi v&agrave; b&agrave;n ghế cho ph&ugrave; hợp để tr&aacute;nh những dị tật về xương sống sau n&agrave;y.',NULL,NULL,'blog','blog:post','blog:posts','29','blog/2014/09/bien-dang-co-the-vi-thuong-xuyen-ngoi-sai-tu-the','admin/blog/edit/29','admin/blog/delete/29'),(269,'Tạo góc bo dễ dàng với jQuery Corner Plugin','Corner plugin gi&uacute;p bo tr&ograve;n c&aacute;c thẻ HTML m&agrave; kh&ocirc;ng cần d&ugrave;ng đến ảnh background. Plugin cung cấp cho bạn rất nhiều lựa chọn về bo g&oacute;c.\r\n\r\n\r\n\r\nC&aacute;ch sử dụng\r\nTrước ti&ecirc;n, bạn cần download jQuery v&agrave; Corner plugin. Sau đ&oacute; th&ecirc;m v&agrave;o trong thẻ &lt;head&gt; của trang web. Bạn c&oacute; thể dowload corner plugin tại: http://malsup.github.io/jquery.corner.js\r\n\r\nC&aacute;c mẫu g&oacute;c bo\r\nMặc định l&agrave; g&oacute;c bo tr&ograve;n\r\n\r\nTuỳ chọn g&oacute;c bo\r\nSử dụng top, bottom, left, right, tl, tr, bl, br để tuỳ chọn vị tr&iacute; g&oacute;c bo\r\n\r\n\r\nX&aacute;c định k&iacute;ch thước g&oacute;c bo\r\nTh&ecirc;m gi&aacute; trị px để x&aacute;c định k&iacute;ch thước g&oacute;c bo\r\n\r\n\r\nLồng hiệu ứng\r\n\r\n\r\nChọn m&agrave;u\r\n\r\nBạn c&oacute; thể xem th&ecirc;m c&aacute;c c&aacute;ch tuỳ biến của plugin corner tr&ecirc;n trang: http://jquery.malsup.com/corner/',NULL,NULL,'blog','blog:post','blog:posts','30','blog/2014/09/tao-goc-bo-de-dang-voi-jquery-corner-plugin','admin/blog/edit/30','admin/blog/delete/30'),(274,'Responsive Web Design sự lựa chọn cho các nhà thiết kế web','Responsive web design l&agrave; một kh&aacute;i niệm d&agrave;nh cho c&aacute;c nh&agrave; thiết kế v&agrave; ph&aacute;t triển ứng dụng web nhằm đ&aacute;p ứng v&agrave; th&iacute;ch nghi được với m&ocirc;i trường (hay thiết bị) của người d&ugrave;ng về k&iacute;ch thước m&agrave;n h&igrave;nh, platform, trạng th&aacute;i xoay hay đứng như: giao diện trang web phải th&iacute;ch nghi được tr&ecirc;n từng loại thiết bị d&ugrave; cho người đ&oacute; d&ugrave;ng Iphone, iPad, netbook, BlackBerry, Kindle hay những chiếc điện thoại di động c&oacute; m&agrave;n h&igrave;nh nhỏ.\r\n\r\nKh&aacute;i niệm về Responsive web design\r\nResponsive web design kh&ocirc;ng phải l&agrave; &yacute; tưởng ho&agrave;n to&agrave;n mới, n&oacute; kế thừa v&agrave; tương tự như responsive architecture, trang web khi được thiết kế phải c&oacute; khả năng tự động điều chỉnh để th&iacute;ch nghi với nhiều nh&oacute;m người sử dụng kh&aacute;c nhau. Responsive web design kh&ocirc;ng chỉ l&agrave; việc t&ugrave;y chỉnh cho ph&ugrave; hợp với k&iacute;ch thước m&agrave;n h&igrave;nh v&agrave; resize lại h&igrave;nh ảnh, m&agrave; n&oacute; mở ra một c&aacute;ch nghĩ ho&agrave;n to&agrave;n mới về thiết kế.&nbsp;\r\n\r\nĐộ ph&acirc;n giải m&agrave;n h&igrave;nh\r\nC&agrave;ng nhiều thiết bị, c&agrave;ng nhiều độ ph&acirc;n giải kh&aacute;c nhau, sự phổ biến của những thiết bị như iPhone, iPad v&agrave; c&aacute;c smartphone c&oacute; thể chuyển từ dạng xem ngang v&agrave; đứng một c&aacute;ch nhanh ch&oacute;ng.\r\n\r\n&nbsp;\r\n\r\nHơn nữa khi thiết kế cho cả m&agrave;n h&igrave;nh nằm ngang v&agrave; m&agrave;n h&igrave;nh đứng, ch&uacute;ng ta phải t&iacute;nh đến h&agrave;ng trăm k&iacute;ch thước m&agrave;n h&igrave;nh kh&aacute;c nhau. Ta c&oacute; thể nh&oacute;m một số k&iacute;ch thước lại với nhau v&agrave; thiết kế cho từng nh&oacute;m một, v&agrave; thiết kế ri&ecirc;ng cho từng nh&oacute;m n&agrave;y nếu cần thiết.&nbsp;\r\n\r\nFlexible layout\r\n\r\n\r\n\r\nH&igrave;nh ảnh cần phải được tự động điều chỉnh v&agrave; kh&ocirc;ng được ph&eacute;p l&agrave;m vỡ cấu tr&uacute;c website, v&igrave; ta kh&ocirc;ng l&agrave;m một sản phẩm in ấn. Khi k&iacute;ch thước ảnh kh&ocirc;ng bị fix ở một gi&aacute; trị n&agrave;o đ&oacute;, n&oacute; mang lại nhiều lợi &iacute;ch hơn mong đợi, &yacute; tưởng tuyệt vời cho những những thiết bị c&oacute; thể xoay ngang v&agrave; đứng.\r\n\r\nFilament Group&rsquo;s Responsive Images\r\n\r\n&nbsp;\r\n\r\nKỹ thuật n&agrave;y được giới thiệu bởi Filement Group, theo đ&oacute; thay v&igrave; resize lại h&igrave;nh ảnh, sẽ load hẳn một ảnh kh&aacute;c khi cần, c&aacute;ch n&agrave;y c&oacute; thể tiết kiệm được việc load ảnh lớn kh&ocirc;ng cần thiết với c&aacute;c thiết bị nhỏ.\r\n\r\nMedia Queries\r\nCSS 3 vẫn hỗ trợ đầy đủ media type từ CSS2.1 như screen, print, handheld, v&agrave; đ&atilde; bổ sung kh&aacute; nhiều khai b&aacute;o mới như max-width, device-width, orientation, color. May mắn thay những thiết bị chạy android, ipad đều kh&ocirc;ng sử dụng tr&igrave;nh duyệt IE, n&ecirc;n bạn an t&acirc;m khi sử dụng những thuộc t&iacute;nh css 3.\r\n\r\nẨn hoặc hiển thị nội dung\r\nR&otilde; r&agrave;ng việc chỉ hiển thị những nội dung thật sự cần thiết tr&ecirc;n những thiết bị c&oacute; k&iacute;ch thước nhỏ l&agrave; cần thiết, kinh nghiệm cho thấy tr&ecirc;n c&aacute;c thiết bị di động bạn chỉ n&ecirc;n c&oacute; một thanh navigation đơn giản, nhấn mạnh nội dung, xem dạng list nhiều d&ograve;ng tốt hơn l&agrave; chia th&agrave;nh nhiều cột\r\n\r\nTouchescreen vs Cursors\r\n\r\n&nbsp;\r\n\r\nM&agrave;n h&igrave;nh cảm ứng ng&agrave;y c&agrave;ng phổ biến. Những thiết bị c&oacute; m&agrave;n h&igrave;nh cảm ứng thường c&oacute; k&iacute;ch thước nhỏ, r&otilde; r&agrave;ng bạn phải thấy được rằng việc tương t&aacute;c qua ng&oacute;n tay cần một khoản kh&ocirc;ng gian lớn hơn như khi d&ugrave;ng chuột, m&agrave;n h&igrave;nh cảm ứng cũng kh&ocirc;ng c&oacute; trạng th&aacute;i hover.\r\n\r\nTổng Kết\r\nWeb design kh&ocirc;ng đơn thuần l&agrave; tạo ra sản phẩm đẹp trong mắt người d&ugrave;ng m&agrave; c&ograve;n l&agrave; c&ocirc;ng việc tạo ra trải nghiệm mới cho người d&ugrave;ng. Responsive web design nếu được ứng dụng tốt sẽ tăng trải nghiệm người d&ugrave;ng kh&ocirc;ng những thế c&ograve;n giải quyết vấn đề hiển thị tr&ecirc;n nhiều loại thiết bị, mọi nền tảng, n&oacute; sẽ c&ograve;n được tiếp tục cải thiện trong nhiều năm tới.',NULL,NULL,'blog','blog:post','blog:posts','32','blog/2014/09/responsive-web-design-su-lua-chon-cho-cac-nha-thiet-ke-web','admin/blog/edit/32','admin/blog/delete/32'),(127,'Lập nghiệp với một nghề','C&oacute; mu&ocirc;n lối v&agrave;o đời đ&acirc;u chỉ l&agrave; phải bước qua ngưỡng cửa ĐH. C&oacute; những con đường v&agrave;o đời c&ograve;n c&oacute; nghĩa l&agrave; đồng h&agrave;nh c&ugrave;ng nỗi vất vả của mẹ, nhọc nhằn của cha... Những học tr&ograve; nhận học bổng Nhất nghệ tinh 2014 đ&atilde; chọn cho m&igrave;nh lối đi đ&oacute;: vừa học vừa l&agrave;m để lập th&acirc;n nhưng vẫn phụ gi&uacute;p gia đ&igrave;nh.\r\n&nbsp;\r\n\r\n\r\n\r\n&Ocirc;ng Vũ Văn B&igrave;nh - Ph&oacute; tổng bi&ecirc;n tập b&aacute;o Tuổi Trẻ trao qu&agrave; lưu niệm cho nh&agrave; t&agrave;i trợ v&agrave; c&aacute;c đơn vị phối hợp thực hiện chương tr&igrave;nh - Ảnh: Quang Định\r\n\r\nHọc bổng Nhất nghệ tinh thuộc chương tr&igrave;nh &ldquo;V&igrave; ng&agrave;y mai ph&aacute;t triển&rdquo; lần 370 của b&aacute;o&nbsp;Tuổi Trẻ.&nbsp;Năm nay học bổng trao cho 200 học sinh ngh&egrave;o vượt kh&oacute; tại 7 tỉnh th&agrave;nh Đ&ocirc;ng Nam Bộ, mỗi suất học bổng trị gi&aacute; 5 triệu đồng.\r\n\r\n&nbsp;\r\n\r\n\r\n	\r\n		\r\n			\r\n		\r\n		\r\n			Bạn Nguyễn Thị Huyền My, sinh vi&ecirc;n Trường Trung cấp Y Dược Hồng Đức x&uacute;c động khi kể c&acirc;u chuyện ho&agrave;n cảnh gia đ&igrave;nh m&igrave;nh - Ảnh: Quang Định\r\n		\r\n	\r\n\r\n\r\nChắt chiu cho học sinh ngh&egrave;o\r\n\r\nS&aacute;ng 18-7, 200 học sinh nghề tại 7 tỉnh th&agrave;nh Đ&ocirc;ng Nam Bộ đ&atilde; c&oacute; mặt tại Nh&agrave; văn ho&aacute; Thanh ni&ecirc;n, TP.HCM nhận học bổng Nhất nghệ tinh.\r\n\r\n&Ocirc;ng Vũ Văn B&igrave;nh, Ph&oacute; TBT b&aacute;o Tuổi Trẻ- đại diện ban tổ chức chia sẻ: &ldquo;Bước v&agrave;o m&ugrave;a khai giảng năm học mới, nhiều cổng trường ĐH CĐ trong cả nước đang mở rộng đ&oacute;n h&agrave;ng triệu học sinh, sinh vi&ecirc;n đến với giảng đường. Nhưng b&ecirc;n cạnh đ&oacute; cũng c&oacute; h&agrave;ng ng&agrave;n bạn trẻ ở c&aacute;c l&agrave;ng qu&ecirc; ngh&egrave;o, v&ugrave;ng s&acirc;u, v&ugrave;ng xa v&agrave; ngay cả c&aacute;c đ&ocirc; thị lớn vẫn canh c&aacute;nh nỗi lo t&uacute;ng thiếu khi thực hiện mơ ước học một nghề cho tương lai. Đ&oacute; l&agrave; l&iacute; do m&agrave; học bổng Nhất nghệ tinh ra đời năm 2009 v&agrave; vẫn tiếp tục được duy tr&igrave; tới h&ocirc;m nay.&rdquo;\r\n\r\n&Ocirc;ng Vũ Duy Hải, chủ tịch HĐQT c&ocirc;ng ty Vina Cam (đại diện cho c&aacute;c nh&agrave; t&agrave;i trợ Học bổng) d&iacute; dỏm n&oacute;i Quỹ học bổng giống như Quỹ &ldquo;b&igrave;nh ổn gi&aacute;&rdquo; trước cơn lốc kh&oacute; khăn của kinh tế hiện tại. &Ocirc;ng Hải khiến nhiều người x&uacute;c động khi kể về c&acirc;u chuyện một nh&acirc;n vi&ecirc;n c&ocirc;ng ty ở huyện H&oacute;c M&ocirc;n của c&ocirc;ng ty &ocirc;ng chắt chiu từng đồng để lo cho gia đ&igrave;nh ngo&agrave;i giờ l&agrave;m bằng việc c&ocirc;ng d&eacute;p v&agrave;o ban đ&ecirc;m nhưng năm n&agrave;o cũng đ&oacute;ng g&oacute;p cho Quỹ.\r\n\r\nTấm l&ograve;ng ấy ch&iacute;nh l&agrave; t&acirc;m trạng chung của những nh&agrave; t&agrave;i trợ khi hướng về một lớp thợ l&agrave;nh&nbsp; nghề hữu &iacute;ch cho đất nước trong tương lai, &ocirc;ng Hải đ&uacute;c kết.\r\n\r\nBước v&agrave;o năm Năm thứ 6, Quỹ học bổng Nhất nghệ tinh do giải Golf g&acirc;y Quỹ Tiếp sức đến trường ng&agrave;y c&agrave;ng c&oacute; th&ecirc;m nhiều mạnh thường qu&acirc;n tham gia. &Ocirc;ng Hải bộc bạch: &ldquo;Số tiền học bổng tuy kh&ocirc;ng nhiều&nbsp;nhưng ch&uacute;ng t&ocirc;i hy vọng đ&oacute; sẽ l&agrave; c&uacute; h&iacute;ch quan trọng để đẩy một thế hệ trẻ l&ecirc;n đỉnh dốc của th&agrave;nh c&ocirc;ng trong tương lai. Ch&uacute;ng t&ocirc;i cũng biểu dương, khen ngợi sự lựa chọn th&ocirc;ng minh của c&aacute;c bạn trẻ khi chọn c&aacute;nh cửa trường nghề để học giỏi một nghề m&agrave; lập nghiệp. N&oacute; g&oacute;p phần tạo n&ecirc;n một thế hệ c&ocirc;ng nh&acirc;n mới c&oacute; tri thức v&agrave; l&agrave;nh nghề đ&oacute;ng g&oacute;p cho sự phồn vinh của đất nước.&rdquo;.\r\n\r\n\r\n	\r\n		\r\n			\r\n		\r\n		\r\n			&Ocirc;ng Vũ Duy Hải&nbsp;- Chủ tịch HĐQT, Tổng gi&aacute;m đốc C&ocirc;ng ty CP Vinacam, đại diện Giải G&ocirc;n g&acirc;y quỹ Tiếp sức đến trường trao học bổng cho c&aacute;c sinh vi&ecirc;n tại buổi lễ - Ảnh: Quang Định\r\n		\r\n	\r\n\r\n\r\n\r\n	\r\n		\r\n			\r\n		\r\n		\r\n			C&ocirc; Nguyễn Thị L&yacute;&nbsp;- Hiệu trường Trường Cao đăng C&ocirc;ng nghệ Thủ Đức trao học bổng cho c&aacute;c sinh vi&ecirc;n tại buổi lễ - Ảnh: Quang Định\r\n		\r\n	\r\n\r\n\r\nVượt kh&oacute; r&egrave;n nghề để lập th&acirc;n\r\n\r\nPhần giao lưu với hai ho&agrave;n cảnh ti&ecirc;u biểu trong Lễ trao học bổng Nhất nghệ tinh khiến nhiều người tham dự x&uacute;c động. Bởi b&ecirc;n cạnh &yacute; ch&iacute; học tập của c&aacute;c bạn trẻ c&ograve;n l&agrave; c&aacute;ch m&agrave; họ vượt qua kh&oacute; khăn. Hai c&ocirc; g&aacute;i nhỏ nhắn Nguyễn Thị Huyền My v&agrave; Trần Ngọc Mỹ x&uacute;c động khi kể về c&acirc;u chuyện gia đ&igrave;nh. Trần Ngọc Mỹ học sinh trường Cao đẳng nghề du lịch Vũng T&agrave;u khiến bạn b&egrave; kh&acirc;m phục về &yacute; ch&iacute; vượt kh&oacute; c&ugrave;ng gia đ&igrave;nh.\r\n\r\nL&uacute;c nhỏ, Mỹ vốn được sống trong gia đ&igrave;nh kinh tế kh&aacute; nhưng do em trai bị bệnh, cha mẹ phải dồn sức chữa trị n&ecirc;n cuối c&ugrave;ng nh&agrave; cửa phải b&aacute;n hết, bản th&acirc;n Mỹ su&yacute;t phải nghỉ học năm lớp 9. Nhưng kh&ocirc;ng đầu h&agrave;ng số phận, Mỹ quyết t&acirc;m vừa học vừa l&agrave;m để phụ gi&uacute;p gia đ&igrave;nh. Mới hơn 60 tuổi nhưng b&agrave; L&ecirc; Thị Nga, mẹ của Mỹ đ&atilde; gi&agrave; yếu nhiều so với tuổi v&igrave; l&agrave;m lụng qu&aacute; sức. Ng&agrave;y ng&agrave;y, b&agrave; đạp xe h&agrave;ng chục c&acirc;y số đi b&aacute;n v&eacute; số m&agrave; cũng chỉ được 100 ng&agrave;n đồng. G&aacute;nh nặng gia đ&igrave;nh dồn l&ecirc;n vai người mẹ lẽ ra phải được nghỉ ngơi ở tuổi ngo&agrave;i 60 khi b&agrave; c&ugrave;ng l&uacute;c phải nu&ocirc;i cậu con trai bị t&acirc;m thần v&agrave; hai đứa ch&aacute;u nội bị cha mẹ bỏ rơi.\r\n\r\nC&ograve;n với c&ocirc; dược sĩ tương lai Nguyễn Thị Huyền My th&igrave; h&agrave;nh tr&igrave;nh trọ học ở S&agrave;i G&ograve;n gắn liền với quang g&aacute;nh tảo tần của người mẹ. Mẹ của Huyền My đ&atilde; v&agrave;o S&agrave;i G&ograve;n 20 năm mưu sinh, gửi những đồng tiền thấm đẫm mồ h&ocirc;i nước mắt từ g&aacute;nh t&agrave;u hũ rong ruổi khắp đường phố. Nhắc đến mẹ l&agrave; Huyền My kh&ocirc;ng n&eacute;n được x&uacute;c động, đ&oacute; c&oacute; lẽ l&agrave; động lực lớn lao để My sắp ho&agrave;n th&agrave;nh chương tr&igrave;nh học Dược sĩ, t&igrave;m c&ocirc;ng việc ổn định gi&uacute;p cha mẹ bớt cực khổ.\r\n\r\n\r\n	\r\n		\r\n			\r\n		\r\n		\r\n			C&aacute;c bạn sinh vi&ecirc;n chụp ảnh lưu niệm c&ugrave;ng c&aacute;c đại biểu tại buổi lễ trao học bổng - Ảnh: Quang Định\r\n		\r\n	\r\n\r\n\r\n\r\n	\r\n		\r\n			\r\n		\r\n		\r\n			Sinh vi&ecirc;n Phạm Văn Q&uacute;y, Trường Trung cấp Bến Th&agrave;nh ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin phải nhờ bạn của m&igrave;nh c&otilde;ng l&ecirc;n s&acirc;n khấu nhận học bổng - Ảnh: Quang Định\r\n		\r\n	\r\n\r\n\r\nH&agrave;ng năm, cứ đến mu&agrave; tuyển sinh ĐH&nbsp;- CĐ l&agrave; h&agrave;ng triệu học sinh c&ugrave;ng cha mẹ n&ocirc; nức đi thi, h&ograve;ng mong được bước v&agrave;o một c&aacute;nh cổng ĐH l&agrave;m rạng danh d&ograve;ng họ. Rất &iacute;t bạn trẻ trong số ấy chọn cho m&igrave;nh con đương học nghề khi những c&aacute;nh cửa trường nghề lu&ocirc;n rộng mở. Theo thống k&ecirc; của Bộ LĐTBXH, chỉ c&oacute; khoảng 10% học sinh sau khi tốt nghiệp Phổ th&ocirc;ng chọn học nghề lập th&acirc;n. C&oacute; thể với những học tr&ograve; được nhận học bổng Nhất nghệ tinh do ho&agrave;n cảnh kh&oacute; khăn kh&ocirc;ng cho ph&eacute;p họ mơ về một ng&ocirc;i trường ĐH để tiếp tục d&ugrave;i m&agrave;i tri thức. Nhưng tin rằng, với nỗ lực v&agrave; sự lựa chọn s&aacute;t sườn cuộc sống, tương lai sẽ tươi s&aacute;ng với c&aacute;c bạn trẻ n&agrave;y khi nhu cầu về người lao động c&oacute; tay nghề cao ng&agrave;y c&agrave;ng bức thiết ở bất kỳ doanh nghiệp n&agrave;o.\r\n\r\nH&igrave;nh ảnh cậu học tr&ograve;&nbsp;- c&ocirc;ng nh&acirc;n B&ugrave;i Quốc Hưng ở Củ Chi l&ecirc;n nhận học bổng h&ocirc;m nay khiến ch&uacute;ng t&ocirc;i c&agrave;ng tin v&agrave;o điều đ&oacute;. Hưng được thầy phụ tr&aacute;ch dẫn l&ecirc;n nhận học bổng trong bộ đồng phục của trường Cao đẳng B&aacute;ch khoa S&agrave;i G&ograve;n. Thầy phụ tr&aacute;ch của Hưng kể: &ldquo;Đi nhận học bổng m&agrave; em mang d&eacute;p, t&ocirc;i phải nhắc em mượn bạn đ&ocirc;i gi&agrave;y mang v&agrave;o đấy&rdquo;. V&agrave; cậu học tr&ograve; ngh&egrave;o của ch&uacute;ng t&ocirc;i lần đầu ngượng ngịu đ&ocirc;i ch&uacute;t trong đ&ocirc;i gi&agrave;y ba ta chạy bộ của bạn. Hưng ngại ng&ugrave;ng bảo: &ldquo;Xưa giờ em c&oacute; đi gi&agrave;y bao giờ đ&acirc;u, đi học với đi l&agrave;m to&agrave;n đi d&eacute;p kh&ocirc;ng &agrave;&rdquo;. Mộc mạc l&agrave; thế nhưng ph&iacute;a sau c&acirc;u chuyện về đ&ocirc;i gi&agrave;y của Hưng l&agrave; một ước mơ đẹp th&agrave;nh thầy thuốc trong tương lai.\r\n\r\nTin rằng, từ đ&ocirc;i ch&acirc;n mang đ&ocirc;i gi&agrave;y tuềnh to&agrave;ng h&ocirc;m nay, nhiều bạn trẻ kh&aacute;c nhận học bổng Nhất nghệ tinh với niềm tin v&agrave; ho&agrave;i b&atilde;o sẽ tự tin ghi lại dấu ch&acirc;n th&agrave;nh đạt của ch&iacute;nh m&igrave;nh trong tương lai.\r\n\r\n&nbsp;\r\n\r\n&nbsp; &nbsp; &nbsp; &nbsp; L&Ecirc; V&Acirc;N\r\n\r\n(Theo&nbsp;Tuổi trẻ)',NULL,NULL,'blog','blog:post','blog:posts','20','blog/2014/08/lap-nghiep-voi-mot-nghe','admin/blog/edit/20','admin/blog/delete/20'),(276,'HTML5 – Cảm ứng trên các thiết bị di động','Hiện nay c&aacute;c nh&agrave; ph&aacute;t triển đang ph&aacute;t triển để HTML5 ng&agrave;y c&agrave;ng ho&agrave;n thiện hơn v&agrave; trong thời đại hiện nay c&aacute;c thiết bị cảm ứng ph&aacute;t triển mạnh v&agrave; nhất l&agrave; c&aacute;c smartphone cảm ứng như Iphone, Sony, HTC, Samsung, Motorola,...\r\n\r\nB&ecirc;n cạnh đ&oacute; cũng c&oacute; 1 số thư viện hỗ trợ ch&uacute;ng ta trong việc diễn hoạt v&agrave; tr&igrave;nh diễn tr&ecirc;n mobile th&ocirc;ng qua c&aacute;c sự kiện &ldquo;event touch&rdquo;, ch&uacute;ng ta c&oacute; thể dựa v&agrave;o bộ thư viện m&agrave; ch&uacute;ng ta y&ecirc;u th&iacute;ch để ph&aacute;t triển v&agrave; t&ugrave;y chỉnh.&nbsp;\r\n\r\nTrong HTML5 hiện tại c&oacute; hỗ trợ một số event touch như sau:\r\n\r\n1.&nbsp;&nbsp; &nbsp;ontouchcancel\r\n2.&nbsp;&nbsp; &nbsp;ontouchend\r\n3.&nbsp;&nbsp; &nbsp;ontouchenter\r\n4.&nbsp;&nbsp; &nbsp;ontouchleave\r\n5.&nbsp;&nbsp; &nbsp;ontouchmove\r\n6.&nbsp;&nbsp; &nbsp;ontouchstart\r\n\r\nMột số thư viện hỗ trợ:\r\n\r\n1.&nbsp;&nbsp; &nbsp;Sencha Touch\r\n2.&nbsp;&nbsp; &nbsp;jQuery Mobile\r\n3.&nbsp;&nbsp; &nbsp;Kinetic\r\n4.&nbsp;&nbsp; &nbsp;jQTouch\r\n5.&nbsp;&nbsp; &nbsp;V&agrave; một số thư viện kh&aacute;c như DHTMLX Touch, MagicTouch, Titanium, &nbsp;&hellip;\r\n\r\nT&ugrave;y trưởng hợp v&agrave; y&ecirc;u cầu m&agrave; ch&uacute;ng ta n&ecirc;n c&acirc;n nhắc sử dụng những thư viện n&oacute;i tr&ecirc;n:\r\n\r\n1.&nbsp;&nbsp; &nbsp;Do y&ecirc;u cầu của kh&aacute;ch h&agrave;ng\r\n2.&nbsp;&nbsp; &nbsp;Do thư viện n&agrave;y kh&ocirc;ng hỗ trợ m&agrave; c&aacute;c thư viện kh&aacute;c hỗ trợ (đ&ograve;i hỏi phải hiểu nhiều thư viện)\r\n3.&nbsp;&nbsp; &nbsp;Hoặc ch&uacute;ng ta c&oacute; thể viết th&ecirc;m để sử dụng những g&igrave; m&agrave; thư viện kh&ocirc;ng hỗ trợ...\r\n\r\nSo s&aacute;nh một số ưu điểm v&agrave; nhược điểm của những thư viện n&oacute;i tr&ecirc;n\r\n&bull;&nbsp;&nbsp; &nbsp;jQTouch : l&agrave; dễ d&agrave;ng để sử dụng . Th&iacute;ch hợp trong việc x&acirc;y dựng c&aacute;c ứng dụng cho iPhone với HTML, CSS, v&agrave; JavaScript . jQTouch c&oacute; một c&aacute;ch tiếp cận tiến bộ cung cấp một thiết lập cơ bản của c&aacute;c vật dụng v&agrave; h&igrave;nh ảnh động. Tuy nhi&ecirc;n, hiệu suất của thư viện hoạt động kh&ocirc;ng tốt.\r\n&bull;&nbsp;&nbsp; &nbsp;jQuery Mobile : ra đời v&agrave;o khoảng th&aacute;ng 8 năm 2010 , nhưng vẫn được nhiều người biết đến do t&iacute;nh phổ biến, dễ hiểu v&agrave; được nhiều lập tr&igrave;nh vi&ecirc;n sử dụng v&agrave; viết c&aacute;c plugin bổ sung miễn ph&iacute;. jQuery mobile mạnh hơn jQTouch về mặt hiệu suất, c&aacute;c hiệu ứng chuyển động tốt hơn. Nhưng jQuery mobile chưa được ho&agrave;n thiện ở giai đoạn hiện tại v&igrave; n&oacute; ra đời sau v&agrave; c&ograve;n kh&aacute; non trẻ. Tuy nhi&ecirc;n, trong tương lai gần jQuery mobile c&oacute; thể l&agrave; 1 thư viện hỗ trợ mạnh mẽ c&ocirc;ng nghệ HTML5 cho c&aacute;c thiết bị di động cảm ứng.\r\n&bull;&nbsp;&nbsp; &nbsp;Sencha Touch : l&agrave; một thư viện c&oacute; hiệu suất tốt hơn, bắt buộc sử dụng dữ liệu kiểu Json v&agrave; tập trung v&agrave;o việc xử l&yacute; c&aacute;c đối tượng DOM thay v&igrave; HTML như jQuery mobile v&agrave; jQTouch. &nbsp;Theo cộng đồng v&agrave; t&igrave;m hiểu tr&ecirc;n mạng th&igrave; đ&acirc;y l&agrave; 1 thư viện tốt nhất hiện nay nhưng lại c&oacute; t&iacute;nh ph&iacute;. Việc sử dụng n&oacute; với ph&iacute; l 299$/1 năm l&agrave; một mặt hạn chế so với xu thế phần mềm m&atilde; nguồn mở hiện nay.\r\n&bull;&nbsp;&nbsp; &nbsp;Kinetic : l&agrave; 1 thư viện kh&ocirc;ng t&iacute;nh ph&iacute;, hoạt động ổn định v&agrave; hỗ trợ người d&ugrave;ng viết ứng dụng HTML5 tr&ecirc;n nền 2D v&agrave; 3D với 2 phi&ecirc;n bản kh&aacute;c nhau v&agrave; li&ecirc;n tục được cập nhật. Hỗ trợ mạnh mẽ c&aacute;c diễn hoạt tr&ecirc;n nền HTML5, c&aacute;c phương thức quản l&yacute; v&agrave; kiểm so&aacute;t một đối tượng bất kỳ khi ch&uacute;ng được th&ecirc;m v&agrave;o khung vẽ. Tuy nhi&ecirc;n , hiệu suất hoạt động chưa tốt khi thực hiện tr&ecirc;n 1 số điện thoại cấu h&igrave;nh thấp.\r\nDựa v&agrave;o bảng so s&aacute;nh tr&ecirc;n, t&ugrave;y v&agrave;o trường hợp v&agrave; ưu điểm của từng thư viện m&agrave; ch&uacute;ng ta c&oacute; thể sử dụng ch&uacute;ng để hỗ trợ trong c&ocirc;ng việc.',NULL,NULL,'blog','blog:post','blog:posts','34','blog/2014/09/html5-cam-ung-tren-cac-thiet-bi-di-dong','admin/blog/edit/34','admin/blog/delete/34'),(277,'PHP5 và những cải tiến vượt bậc','PHP hiện l&agrave; ng&ocirc;n ngữ lập tr&igrave;nh được sử dụng phổ biến nhất để x&acirc;y dựng c&aacute;c trang web động với 16.251.453 t&ecirc;n miền, v&agrave; 1.346.521 địa chỉ IP, t&iacute;nh đến ng&agrave;y 18/7/2004.&nbsp;\r\n\r\nPHP 5.0 c&oacute; những cải tiến vượt bậc so với c&aacute;c bản PHP 4.x trước đ&acirc;y. Cho đến nay, PHP 5 được coi l&agrave; bản cập nhật đ&aacute;ng kể nhất trong v&ograve;ng 2 năm trở lại đ&acirc;y trong khi vẫn giữ được thế mạnh vốn c&oacute; của PHP kể từ thời PHP/FI 2.0 l&agrave; tốc độ v&agrave; t&iacute;nh hiệu quả. (Tham khảo lịch sử ph&aacute;t triển PHP tr&ecirc;n Wikipedia tại địa chỉ http://vi.wikipedia.org/wiki/PHP v&agrave; c&aacute;c th&ocirc;ng tin kh&aacute;c về PHP tr&ecirc;n website của cộng đồng PHP Việt Nam tại địa chỉ http://www.phpvietnam.net).&nbsp;\r\n\r\nTrở lại năm 1998, việc đưa Zend Engine 1 v&agrave;o bộ l&otilde;i của PHP Engine đ&atilde; đem đến sức mạnh vượt trội của PHP 4. Mặc d&ugrave; PHP đ&atilde; thu h&uacute;t được số lượng người d&ugrave;ng đ&ocirc;ng đảo nhưng hạn chế ch&iacute;nh của n&oacute; l&agrave; kh&ocirc;ng cung cấp th&ecirc;m c&ocirc;ng cụ để x&acirc;y dựng website.&nbsp;\r\n\r\nS&aacute;u năm sau đ&oacute;, cộng đồng đ&atilde; thực sự nghi&ecirc;m t&uacute;c xem x&eacute;t lại những tồn tại cố hữu của PHP khiến c&aacute;c nh&agrave; ph&aacute;t triển gặp nhiều kh&oacute; khăn khi xử l&yacute; một số vấn đề. Trong số đ&oacute;, c&aacute;c nh&agrave; ph&aacute;t triển nhấn mạnh đến việc hỗ trợ lập tr&igrave;nh hướng đối tượng trong PHP 4 một c&aacute;ch nửa vời, thư viện mở rộng&nbsp;MySQL kh&ocirc;ng hỗ trợ giao thức m&aacute;y kh&aacute;ch&nbsp;MySQL 4.1 mới, hỗ trợ&nbsp;XML&nbsp;chắp v&aacute; v&agrave; hỗn độn.&nbsp;\r\n\r\nPHP 5 ra đời đ&atilde; cải thiện 3 vấn đề quan trọng tr&ecirc;n (được viết lại ho&agrave;n to&agrave;n) v&agrave; kết quả thực sự ấn tượng. Tuy nhi&ecirc;n, đ&acirc;y vẫn chưa phải l&agrave; tất cả những g&igrave; tốt đẹp nhất m&agrave; PHP 5 mang đến. Trong b&agrave;i viết n&agrave;y, t&ocirc;i sẽ đề cập đến 7 đặc điểm nổi bật của PHP 5. Những đặc điểm n&agrave;y cho ph&eacute;p ph&aacute;t triển m&atilde; lệnh ngắn hơn, đẹp hơn v&agrave; cơ động hơn.&nbsp;\r\n\r\n\r\nHỗ trợ mạnh lập tr&igrave;nh hướng đối tượng&nbsp;\r\nMặc d&ugrave; những t&iacute;nh năng hỗ trợ đối tượng đ&atilde; được tuy&ecirc;n bố từ PHP 4, tuy nhi&ecirc;n cộng đồng c&aacute;c nh&agrave; ph&aacute;t triển đ&atilde; nhanh ch&oacute;ng nhận ra sự non nớt của ch&uacute;ng. Với PHP 5, c&aacute;c nh&agrave; ph&aacute;t triển từ nay c&oacute; trong tay c&aacute;c phương tiện lập tr&igrave;nh hướng đối tượng ho&agrave;n thiện (PHP 5 vẫn cho ph&eacute;p l&agrave;m việc b&igrave;nh thường với kiểu lập tr&igrave;nh cấu tr&uacute;c). C&oacute; thể liệt k&ecirc; một số đặc điểm:&nbsp;\r\n&nbsp;\r\n1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; M&ocirc; h&igrave;nh đối tượng ho&agrave;n to&agrave;n mới dựa tr&ecirc;n nguy&ecirc;n mẫu l&agrave; m&ocirc; h&igrave;nh của Java&nbsp;\r\n2.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; H&agrave;m tạo dựng thống nhất _construct(): tạo điều kiện để di chuyển c&aacute;c lớp từ c&acirc;y ph&acirc;n cấp lớp n&agrave;y sang c&acirc;y ph&acirc;n cấp lớp kh&aacute;c.&nbsp;\r\n3.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; H&agrave;m hủy _destruct(): tạo điều kiện để ghi lại c&aacute;c th&ocirc;ng tin phục vụ cho việc t&igrave;m lỗi, đ&oacute;ng kết nối dữ liệu v&agrave; thực hiện c&aacute;c c&ocirc;ng việc giải ph&oacute;ng t&agrave;i nguy&ecirc;n kh&aacute;c.&nbsp;\r\n4.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&aacute;c thuộc t&iacute;nh v&agrave; c&aacute;c phương thức public, protected, v&agrave; private: cho ph&eacute;p định nghĩa t&iacute;nh hữu h&igrave;nh của c&aacute;c thuộc t&iacute;nh lớp. Điều n&agrave;y rất quan trọng để hỗ trợ t&iacute;nh kế thừa v&agrave; t&iacute;nh bao g&oacute;i trong lập tr&igrave;nh hướng đối tượng.&nbsp;\r\n5.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Giao diện (Interface): t&iacute;nh năng n&agrave;y lấy từ m&ocirc; h&igrave;nh của Java nhằm hỗ trợ đa kế thừa.\r\n6.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&aacute;c lớp trừu tượng.\r\n7.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Cấu tr&uacute;c gợi nhắc kiểu lớp (class hints).&nbsp;\r\n8.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&aacute;c thuộc t&iacute;nh v&agrave; phương thức static.&nbsp;\r\n9.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&aacute;c thuộc t&iacute;nh v&agrave; phương thức Final.\r\n10. Một loạt c&aacute;c phương thức hỗ trợ mạnh kh&aacute;c như auto loading, reflection, xử l&yacute; lỗi ngoại lệ (exceptions), nh&acirc;n bản đối tượng (object cloning), to&aacute;n tử instanceof...&nbsp;\r\n&nbsp;\r\nTh&ecirc;m v&agrave;o đ&oacute;, c&aacute;c đối tượng b&acirc;y giờ đồng thời cả g&aacute;n lẫn truyền đều theo tham chiếu (reference hoặc handle) thay v&igrave; theo gi&aacute; trị, nhờ vậy lập tr&igrave;nh vi&ecirc;n kh&ocirc;ng c&ograve;n phải sử dụng rải r&aacute;c c&aacute;c k&iacute; hiệu &amp; xuy&ecirc;n suốt chương tr&igrave;nh. C&aacute;c t&iacute;nh năng ng&ocirc;n ngữ n&agrave;y cũng đ&atilde; mở đường cho lập tr&igrave;nh vi&ecirc;n PHP sử dụng tối ưu sức mạnh của c&aacute;c m&ocirc; h&igrave;nh thiết kế (pattern).&nbsp;\r\n\r\n\r\nThư viện mở rộng MySQL được viết lại ho&agrave;n to&agrave;n&nbsp;\r\nHệ quản trị CSDL MySQL song h&agrave;nh với PHP như h&igrave;nh với b&oacute;ng, mặc d&ugrave; PHP c&oacute; khả năng hỗ trợ nhiều hệ CSDL kh&aacute;c. Tuy nhi&ecirc;n, trong khi sự kết hợp n&agrave;y đem đến những th&agrave;nh c&ocirc;ng vang dội cho cả hai sản phẩm th&igrave; sự lạc hậu của m&ocirc; đun mở rộng để PHP l&agrave;m việc với&nbsp;MySQL lại l&agrave; một trở ngại khi n&oacute; kh&ocirc;ng cho ph&eacute;p tận dụng c&aacute;c t&iacute;nh năng cực k&igrave; hấp dẫn của&nbsp;MySQL 4.1 v&agrave; c&aacute;c bản cao hơn.&nbsp;\r\nNh&oacute;m ph&aacute;t triển PHP 5 đ&atilde; viết một thư viện mở rộng ho&agrave;n to&agrave;n mới, rất mạnh để hỗ trợ&nbsp;MySQL 4.1 v&agrave; 5.x, mang t&ecirc;n MySQL (MySQL Improved). C&aacute;c t&iacute;nh năng bao gồm:&nbsp;\r\n&nbsp;\r\n1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&acirc;u lệnh bi&ecirc;n dịch trước\r\n2.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tham số xuất v&agrave; nhập c&oacute; giới hạn\r\n3.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Kết nối SSL\r\n4.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; H&agrave;m đa truy vấn.&nbsp;\r\n&nbsp;\r\nMySQL c&ograve;n tận dụng được hỗ trợ lập tr&igrave;nh hướng đối tượng mới của PHP 5 để cung cấp một giao diện hướng đối tượng với&nbsp;MySQL. Tr&ecirc;n hết, những bản mới nhất của&nbsp;MySQL&nbsp;b&acirc;y giờ cho ph&eacute;p d&ugrave;ng chọn lọc con (subselects), chuyển t&aacute;c (transactions) v&agrave; bản sao dữ liệu (replication).&nbsp;\r\n\r\n\r\nBộ c&ocirc;ng cụ&nbsp;XML&nbsp;c&oacute; thể tương t&aacute;c&nbsp;\r\nPHP 5 đ&atilde; cải tiến hỗ trợ&nbsp;XML&nbsp;với c&aacute;c thư viện&nbsp;XML&nbsp;mở rộng như sau:&nbsp;\r\n&nbsp;\r\n1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; L&agrave;m việc được với nhau như một thể thống nhất.&nbsp;\r\n2.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Được chuẩn h&oacute;a tr&ecirc;n một thư viện&nbsp;XML&nbsp;duy nhất: libXML2.&nbsp;\r\n3.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tu&acirc;n thủ ho&agrave;n to&agrave;n c&aacute;c đặc tả của W3.&nbsp;\r\n4.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Xử l&yacute; dữ liệu hiệu quả.&nbsp;\r\n5.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Cung cấp c&ocirc;ng cụ xử l&yacute;&nbsp;XML&nbsp;mạnh.&nbsp;\r\n\r\n\r\nTh&ecirc;m v&agrave;o đ&oacute;, để tiếp tục duy tr&igrave; t&iacute;nh dễ d&agrave;ng của PHP trong việc x&acirc;y dựng ứng dụng web, người ta đ&atilde; đưa v&agrave;o một thư viện&nbsp;XML&nbsp;mới cho ph&eacute;p đọc v&agrave; thay đổi dễ d&agrave;ng c&aacute;c t&agrave;i liệu&nbsp;XML. M&ocirc; đun mở rộng SimpleXML&nbsp;cho ph&eacute;p tương t&aacute;c với c&aacute;c th&ocirc;ng tin c&oacute; trong một văn bản&nbsp;XML&nbsp;như thể c&aacute;c th&ocirc;ng tin n&agrave;y l&agrave; mảng hay đối tượng, c&oacute; thể d&ugrave;ng v&ograve;ng lặp để duyệt qua ch&uacute;ng, bi&ecirc;n tập ch&uacute;ng tại chỗ chỉ bằng c&aacute;ch g&aacute;n c&aacute;c gi&aacute; trị mới v&agrave;o c&aacute;c biến.&nbsp;\r\n\r\nNếu bạn đ&atilde; biết trước định dạng của t&agrave;i liệu, v&iacute; dụ như khi ph&acirc;n t&aacute;ch c&aacute;c file RSS, dữ liệu cấu h&igrave;nh th&igrave; d&ugrave;ng SimpleXMLrất tiện.&nbsp;\r\n\r\nNếu th&iacute;ch d&ugrave;ng DOM, bạn sẽ rất hứng th&uacute; với m&ocirc; đun mở rộng DOM của PHP 5, m&ocirc; đun n&agrave;y đ&atilde; được viết lại v&agrave; cải tiến rất nhiều so với bản trong PHP 4.&nbsp;\r\n\r\n\r\nHệ CSDL nh&uacute;ng SQLITE&nbsp;\r\nSự gắn kết ho&agrave;n hảo với&nbsp;MySQL&nbsp;l&agrave; một lợi thế rất lớn cho PHP 4.x, tuy nhi&ecirc;n nhiều khi ứng dụng web lại kh&ocirc;ng cần nhiều đến khả năng của hệ quản trị CSDL mạnh. Do đ&oacute;, việc ph&acirc;n phối k&egrave;m hệ CSDL nh&uacute;ng SQLite cho ph&eacute;p nh&agrave; ph&aacute;t triển c&oacute; th&ecirc;m c&ocirc;ng cụ sẵn s&agrave;ng để lưu trữ v&agrave; truy vấn dữ liệu bằng phương thức SQL m&agrave; kh&ocirc;ng cần c&agrave;i đặt hệ CSDL ri&ecirc;ng biệt n&agrave;o cả. SQLite xử l&yacute; ch&iacute;nh x&aacute;c c&aacute;c truy cập kh&oacute;a chặn (locking) v&agrave; truy cập đồng thời (concurrent), hai vấn đề nổi cộm khi xử l&yacute; c&aacute;c file dữ liệu phẳng (flat file) tự tạo.&nbsp;\r\n\r\nSQLite đủ mạnh để xử l&yacute; phần lớn c&aacute;c nhu cầu lưu trữ v&agrave; thao t&aacute;c dữ liệu th&ocirc;ng thường. N&oacute; hỗ trợ:&nbsp;\r\n\r\n\r\n1.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Chuyển t&aacute;c (transactions)\r\n2.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Truy vấn con (Subqueries)&nbsp;\r\n3.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Kiểm so&aacute;t to&agrave;n vẹn tham chiếu (triggers)&nbsp;\r\n4.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Nhiều t&iacute;nh năng cao cấp li&ecirc;n quan đến CSDL như hỗ trợ View...&nbsp;\r\n\r\n\r\nSQLite tương th&iacute;ch với phần lớn c&aacute;c t&iacute;nh năng chuẩn SQL92, tuy nhi&ecirc;n bản 3.0 mới nhất chưa hỗ trợ chuyển t&aacute;c lồng nhau, r&agrave;ng buộc kh&oacute;a ngoại, cũng như hỗ trợ kiểm so&aacute;t to&agrave;n vẹn tham chiếu một c&aacute;ch đầy đủ. Bạn thậm ch&iacute; c&oacute; thể viết c&aacute;c h&agrave;m cho người sử dụng tự định nghĩa bằng PHP v&agrave; gọi ch&uacute;ng b&ecirc;n trong SQLite. Đ&acirc;y l&agrave; t&iacute;nh năng nổi bật nhất từ trước đến nay c&oacute; được trong một m&ocirc; đun mở rộng li&ecirc;n quan đến CSDL trong PHP.&nbsp;\r\n\r\n\r\nCơ chế xử l&yacute; lỗi s&aacute;ng sủa hơn với Exceptions&nbsp;\r\nPHP 5 cung cấp một m&ocirc; h&igrave;nh kiểm tra lỗi ho&agrave;n to&agrave;n kh&aacute;c so với PHP 4. N&oacute; c&oacute; t&ecirc;n l&agrave; xử l&yacute; lỗi ngoại lệ. Với lỗi ngoại lệ, nh&agrave; ph&aacute;t triển được giải ph&oacute;ng khỏi việc phải kiểm tra gi&aacute; trị trả lại của tất cả c&aacute;c h&agrave;m. Thay v&agrave;o đ&oacute;, bạn c&oacute; thể t&aacute;ch biệt luận l&yacute; lập tr&igrave;nh xử l&yacute; lỗi v&agrave; đặt ch&uacute;ng nằm b&ecirc;n cạnh c&aacute;c khối m&atilde;.&nbsp;\r\n\r\nLỗi ngoại lệ thường được thấy ở c&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh hướng đối tượng như Java v&agrave; C++. Hiện tại, chỉ c&oacute; một số &iacute;t m&ocirc; đun mở rộng của PHP sử dụng lỗi ngoại lệ, nhưng dần dần điều n&agrave;y sẽ được thực hiện đồng bộ. Tuy nhi&ecirc;n, bạn c&oacute; thể sử dụng lỗi ngoại lệ cho bất cứ đoạn m&atilde; PHP n&agrave;o của m&igrave;nh.&nbsp;\r\n\r\n\r\nBản thực thi SOAP cao cấp&nbsp;\r\nSOAP l&agrave; th&agrave;nh phần chủ yếu của lĩnh vực dịch vụ web đang ph&aacute;t triển rất nhanh. Hỗ trợ SOAP trong PHP 4 chỉ ở cấp trung b&igrave;nh, kh&ocirc;ng chỉ l&agrave; việc c&oacute; rất &iacute;t c&aacute;c g&oacute;i SOAP m&agrave; c&ograve;n ở chỗ c&aacute;c g&oacute;i quan trọng nhất viết bằng PHP thay v&igrave; C n&ecirc;n chậm.&nbsp;\r\n\r\nVới PHP 5, cuối c&ugrave;ng ch&uacute;ng ta đ&atilde; c&oacute; một m&ocirc; đun mở rộng cho SOAP viết bằng C. Hiện tại, m&ocirc; đun n&agrave;y thực thi được phần lớn, mặc d&ugrave; kh&ocirc;ng phải l&agrave; tất cả c&aacute;c đặc điểm n&ecirc;u trong SOAP 1.2. Trong tương lai, m&ocirc; đun n&agrave;y sẽ được cải thiện nhiều hơn. Hiện tại, so với .NET hay Java th&igrave; hỗ trợ SOAP trong PHP vẫn bị xếp ở chiếu dưới.&nbsp;\r\n\r\n\r\nBộ lặp (Iterator)\r\nC&aacute;c bộ lặp l&agrave; t&iacute;nh năng ho&agrave;n to&agrave;n mới trong PHP 5.0. Ch&uacute;ng cho ph&eacute;p bạn sử dụng v&ograve;ng lặp foreach để lặp v&ograve;ng qua c&aacute;c kiểu dữ liệu kh&aacute;c nhau: danh s&aacute;ch thư mục, tập kết quả CSDL v&agrave; cả t&agrave;i liệu&nbsp;XML. SPL - Thư viện PHP chuẩn - l&agrave; tập c&aacute;c bộ lặp cung cấp chức năng n&agrave;y c&ugrave;ng với c&aacute;c chức năng lọc, giới hạn, ghi bộ nhớ đệm v&agrave; thay đổi kết quả bộ lặp.&nbsp;\r\n\r\nBộ lặp l&agrave; c&aacute;ch l&agrave;m thủ c&ocirc;ng rất hay để loại đi tất cả c&aacute;c đoạn m&atilde; g&acirc;y nhiễu trong m&atilde; nguồn của bạn.&nbsp;\r\nV&iacute; dụ, bộ lặp DirectoryIterator biến việc lặp qua c&aacute;c thư mục từ c&aacute;ch viết:&nbsp;\r\n\r\n&nbsp;\r\n&lt;?php&nbsp;\r\n$dir = opendir($path);&nbsp;\r\nwhile (false !== ($file = readdir($dir))) {&nbsp;\r\n&nbsp;&nbsp;print &#39;$file&#39;;&nbsp;\r\n}&nbsp;\r\nclosedir($dir);&nbsp;\r\n?&gt;&nbsp;\r\nth&agrave;nh c&aacute;ch viết:&nbsp;\r\n\r\n&lt;?php&nbsp;\r\nforeach (new DirectoryIterator($path) as $file) {&nbsp;\r\n&nbsp;&nbsp;print $file;&nbsp;\r\n}&nbsp;\r\n?&gt;\r\n&nbsp;\r\nNhư vậy l&agrave; kh&ocirc;ng c&oacute; con trỏ tham chiếu thư mục n&agrave;o nằm lộn xộn v&agrave;o đ&oacute;, cũng như kh&ocirc;ng c&oacute; điều kiện so s&aacute;nh thiếu trong s&aacute;ng n&agrave;o cần phải kiểm tra b&ecirc;n trong v&ograve;ng lặp.&nbsp;\r\n\r\nNgo&agrave;i c&aacute;c điểm nổi bật tr&ecirc;n, PHP 5.0 c&ograve;n cung cấp:&nbsp;cải tiến d&ograve;ng (streams), bộ bao (wrapper) v&agrave; bộ lọc (filter).&nbsp;\r\nD&ograve;ng l&agrave; kh&aacute;i niệm được đưa ra trong PHP 4.3, nhưng trước PHP 5.0 n&oacute; l&agrave; phần &iacute;t được d&ugrave;ng đến. Những cải tiến được đưa v&agrave;o từ bản Beta 3 của PHP 5.0 cho ph&eacute;p đặt giao diện file v&agrave;o việc đọc v&agrave; viết dữ liệu bằng c&aacute;ch sử dụng c&aacute;c đối tượng phụ thuộc theo giao thức, m&agrave; thường được nhắc đến với c&aacute;i t&ecirc;n bộ bao. D&ograve;ng cũng cho ph&eacute;p thay đổi việc truyền d&ograve;ng dữ liệu qua ch&uacute;ng bằng c&aacute;ch đ&iacute;nh k&egrave;m v&agrave;o đ&oacute; c&aacute;c bộ lọc.&nbsp;\r\n\r\n\r\nNội soi m&atilde; bằng c&aacute;ch sử dụng c&aacute;c lớp Reflection.&nbsp;\r\nTập hợp c&aacute;c lớp n&agrave;y cho ph&eacute;p bạn kiểm tra c&aacute;c lớp, c&aacute;c phương thức, c&aacute;c tham số v&agrave; những thứ kh&aacute;c để thăm d&ograve; c&aacute;c thuộc t&iacute;nh của đối tượng. Với ch&uacute;ng, bạn c&oacute; thể dễ d&agrave;ng tạo ra c&aacute;c bộ thăm d&ograve; lớp, c&aacute;c bộ d&ograve; lỗi PHP v&agrave; c&aacute;c c&ocirc;ng cụ kh&aacute;c dựa v&agrave;o việc thu thập c&aacute;c chi tiết về đối tượng v&agrave; h&agrave;m.&nbsp;\r\n\r\n\r\nSinh m&atilde; HTML chuẩn nhờ Tidy.&nbsp;\r\nM&ocirc; đun mở rộng Tidy gi&uacute;p bạn dễ d&agrave;ng nắm chắc việc sinh m&atilde; HTML v&agrave; XHTML hợp lệ. Bộ ph&acirc;n t&aacute;ch th&ocirc;ng minh của n&oacute; thậm ch&iacute; c&ograve;n biến c&aacute;c trang viết m&atilde; t&ugrave;y tiện nhất trở th&agrave;nh c&aacute;c trang tu&acirc;n thủ chặt chẽ c&aacute;c đặc tả mới nhất của W3C.&nbsp;\r\n\r\n\r\nXử l&yacute; d&ograve;ng lệnh ưu việt hơn&nbsp;\r\nPhi&ecirc;n bản d&ograve;ng lệnh PHP5 giờ đ&acirc;y cho ph&eacute;p xử l&yacute; từng d&ograve;ng ri&ecirc;ng lẻ, giống như Perl v&agrave; awk. Bạn c&oacute; thể x&aacute;c định m&atilde; cần chạy ở đầu, ở giữa v&agrave; ở cuối của từng d&ograve;ng trong một file.&nbsp;',NULL,NULL,'blog','blog:post','blog:posts','35','blog/2014/09/php5-va-nhung-cai-tien-vuot-bac','admin/blog/edit/35','admin/blog/delete/35'),(419,'Nghệ thuật tạo thiện cảm trong kỹ năng giao tiếp ứng xử','',NULL,NULL,'pages','pages:page','pages:pages','123','ky-nang/nghe-thuat-tao-thien-cam-trong-ky-nang-giao-tiep-ung-xu','admin/pages/edit/123','admin/pages/delete/123'),(390,'Giới thiệu','',NULL,NULL,'pages','pages:page','pages:pages','85','gioi-thieu','admin/pages/edit/85','admin/pages/delete/85'),(348,'TDC','',NULL,NULL,'pages','pages:page','pages:pages','113','doi-tac/tdc','admin/pages/edit/113','admin/pages/delete/113'),(346,'TDC','',NULL,NULL,'pages','pages:page','pages:pages','110','lien-ket/tdc','admin/pages/edit/110','admin/pages/delete/110'),(428,'Kỹ Sư Phần Mềm','Azuki l&agrave; một c&ocirc;ng ty được bi&ecirc;́t đ&ecirc;́n qua những sản phẩm tranh vẽ tay, những hình vẽ hoạt hình d&ecirc;̃ thương, tranh ch&acirc;n dung cho những đ&ocirc;i uy&ecirc;n...',NULL,NULL,'blog','blog:post','blog:posts','41','blog/2014/11/ky-su-phan-mem','admin/blog/edit/41','admin/blog/delete/41'),(241,'Phan Thị Trinh','',NULL,NULL,'pages','pages:page','pages:pages','51','nhan-su/phan-thi-trinh','admin/pages/edit/51','admin/pages/delete/51'),(242,'Phan Thị Hoài Trinh','',NULL,NULL,'pages','pages:page','pages:pages','52','nhan-su/phan-thi-hoai-trinh','admin/pages/edit/52','admin/pages/delete/52'),(243,'Mai Kỷ Tuyên','',NULL,NULL,'pages','pages:page','pages:pages','53','nhan-su/mai-ky-tuyen','admin/pages/edit/53','admin/pages/delete/53'),(244,'Huỳnh Thị Phương Thủy','',NULL,NULL,'pages','pages:page','pages:pages','54','nhan-su/huynh-thi-phuong-thuy','admin/pages/edit/54','admin/pages/delete/54'),(321,'Nguyễn Ngọc Ánh Mỹ','',NULL,NULL,'pages','pages:page','pages:pages','55','nhan-su/nguyen-ngoc-anh-my','admin/pages/edit/55','admin/pages/delete/55'),(246,'Ngô Minh Anh Thư','',NULL,NULL,'pages','pages:page','pages:pages','56','nhan-su/ngo-minh-anh-thu','admin/pages/edit/56','admin/pages/delete/56'),(247,'Phan Thị Thể','',NULL,NULL,'pages','pages:page','pages:pages